FUNCTION_BLOCK "LGF_AstroClock"
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : Siemens_Digital_Industry
FAMILY : LGF
NAME : LGF_AstroClock
   VAR_INPUT 
      enable : Bool;
      positionGps : "LGF_typeGPS_DD";
      offsetSunrise : Time;
