   END_VAR

   VAR_OUTPUT 
      valid { ExternalWritable := 'False'} : Bool;
      busy { ExternalWritable := 'False'} : Bool;
      error { ExternalWritable := 'False'} : Bool;
      status { ExternalWritable := 'False'} : Word := #STATUS_NO_CALL;
      subFunctionStatus { ExternalWritable := 'False'} : Word;
      actualLocalTime {InstructionName := 'DTL'; LibVersion := '1.0'; ExternalWritable := 'False'} : DTL;
      sunriseTime {InstructionName := 'DTL'; LibVersion := '1.0'; ExternalWritable := 'False'} : DTL;
      sunsetTime {InstructionName := 'DTL'; LibVersion := '1.0'; ExternalWritable := 'False'} : DTL;
      isDaytime { ExternalWritable := 'False'} : Bool;
   END_VAR

   VAR 
      statEnableOld { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
      statBusy { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
      statStatus { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Word := #STATUS_NO_CALL;
      statSubfunctionStatus { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Word;
      statLongitudeHour { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Real;
      statLatitudeRadiant { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Real;
   END_VAR

   VAR_TEMP 
      tempRetval : Word;
      tempSysTime {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
      tempOfficLocTime {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
      tempDate1Jan {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
      tempSunrise {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
      tempSunset {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
      tempTimeZone : Real;
      tempDayOfYear : Real;
      tempAproximateTime : Real;
      tempSunMeanAnomaly : Real;
      tempSunLongitude : Real;
      tempSunMeanRad : Real;
      tempSunLongitudeRadiant : Real;
      tempSunAscension : Real;
      tempSunAscensRad : Real;
      tempSinDeclination : Real;
      tempCosDeclination : Real;
      tempCosLocHourAngle : Real;
      tempLocalHourAngle : Real;
      tempLocalMeanTime : Real;
      tempUTC : Real;
      tempLocalTime : Real;
   END_VAR

   VAR CONSTANT 
      SUBSTATUS_DAYLIGHT_SAVING_TIME : Word := 16#0001;
      NO_ERROR : Word := 16#0000;
      STATUS_EXECUTION_FINISHED_NO_ERROR : Word := 16#0000;
      STATUS_NO_CALL : Word := 16#7000;
      STATUS_IN_OPERATION : Word := 16#7001;
      ERR_LATITUDE_VALUE : Word := 16#8204;
      ERR_LONGITUDE_VALUE : Word := 16#8205;
      ERR_RD_SYS_T : Word := 16#8601;
      ERR_RD_LOC_T : Word := 16#8602;
      DEGREE_0 : Real := 0.0;
      DEGREE_15 : Real := 15.0;
      DEGREE_90 : Real := 90.0;
      DEGREE_180 : Real := 180.0;
      DEGREE_360 : Real := 360.0;
      PI : Real := 3.141593;
      DEGREE_TO_RADIANT : Real := 0.01745329;
      RADIANT_TO_DEGREE : Real := 57.29578;
      SUN_ZENIT : LReal := 1.58534073722818;
      MAX_MIN_OR_SEC_59 : UInt := 59;
      SECONDS_PER_HOUR : Real := 3600.0;
      MINUTES_PER_HOUR : Real := 60.0;
      HOURS_PER_DAY : Real := 24.0;
      MS_PER_HOUR : Real := 3600000.0;
      TIME_ZERO {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
      FIRST_MONTH_AND_DAY : USInt := 1;
      APPROXIMATE_TIME_6 : Int := 6;
      APPROXIMATE_TIME_18 : Int := 18;
      SUN_MEAN_ANOMALY_0_9856 : Real := 0.9856;
      SUN_MEAN_ANOMALY_3_289 : Real := 3.289;
      SUN_TRUE_LONGITUDE_0_02 : Real := 0.02;
      SUN_TRUE_LONGITUDE_1_916 : Real := 1.916;
      SUN_TRUE_LONGITUDE_2_0 : Real := 2.0;
      SUN_TRUE_LONGITUDE_282_634 : Real := 282.634;
      SUN_RIGHT_ASCENSION_0_91764 : Real := 0.91764;
      SUN_RIGHT_ASCENSION_2_0 : Real := 2.0;
      SUN_RIGHT_ASCENSION_2_PI : Real := 6.283185;
      SUN_DECLINATION_0_39782 : Real := 0.39782;
      SUM_LOCAL_HOUR_ANGLE : Real := 1.0;
      LOCAL_MEAN_TIME_0_06571 : Real := 0.06571;
      LOCAL_MEAN_TIME_6_622 : Real := 6.622;
   END_VAR


BEGIN
	REGION BLOCK INFO HEADER
	  //===============================================================================
	  // SIEMENS AG / (c)Copyright 2019
	  //-------------------------------------------------------------------------------
	  // Title:            LGF_AstroClock
	  // Comment/Function: Astronomical clock, optional offset, input format DD
	  //                   "LGF_typeGPS_DD" - GPS position as decimal degree
	  // Library/Family:   LGF
	  // Author:           Siemens Industry Online Support
	  // Tested with:      CPU1214C DC/DC/DC FW:V4.3
	  // Engineering:      TIA Portal (V15.1)
	  // Restrictions:     ENO disabled - error handling done with error and status
	  // Requirements:     PLC (S7-1200 / S7-1500)
	  //-------------------------------------------------------------------------------
	  // Change log table:
	  // Version  | Date       | Expert in charge       | Changes applied
	  //----------|------------|------------------------|------------------------------
	  // 01.00.00 19.08.2015  Siemens Industry Online Support
	  //                      First released version
	  // 01.00.01 01.10.2015  Siemens Industry Online Support
	  //                      T_ADD instruction is replaced with "+"
	  // 01.00.02 16.11.2015  Siemens Industry Online Support
	  //                      "offsetSunrise", "offsetSunset" is calculated in
	  //                      "daytime"
	  //                      Bug fix at "Adjust back TO UTC"
	  // 01.01.00 07.06.2015  Siemens Industry Online Support
	  //                      Add output actSystemTime and actLocalTime
	  // 01.01.01 15.06.2015  Siemens Industry Online Support
	  //                      Add comments
	  // 01.01.02 04.01.2017  Siemens Industry Online Support
	  //                      Bug fix at calculation sunrise and sunset
	  // 01.01.03 20.01.2017  Siemens Industry Online Support
	  //                      Upgrade: TIA V14 Update 1
	  // 01.01.04 22.02.2017  Siemens Industry Online Support
	  //                      Code optimization
	  // 01.01.05 09.07.2018  Siemens Industry Online Support
	  //                      Initialize #tempIntSunrise, #tempIntSunset,#tempDate1Jan 
	  // 01.01.06 17.08.2018  Siemens Industry Online Support
	  //                      Upgrade: TIA V15 Update 2
	  // 01.01.07 23.11.2018  Siemens Industry Online Support
	  //                      Upgrade: TIA V15.1
	  // 01.00.08 30.08.2019  Simatic Systems Support
	  //                      Rename from Astro to AstroClock
	  //                      Update Type name to positionGps - "LGF_typeGPS_DD" - GPS position as decimal degree
	  //                      Refactoring of interface
	  //                      - one input type for GPS data
	  //                      - refactored for better usability
	  //                      - refactoring of whole block to "ENABLE" behavior
	  // 03.00.00 23.04.2020  Simatic Systems Support
	  //                      Set version to V3.0.0, harmonize the version of the whole library
	  // 03.00.01 12.11.2020  Simatic Systems Support
	  //                      Bug fix - not enabled - block still running
	  //                      Insert documentation
	  //===============================================================================
	END_REGION BLOCK INFO HEADER

	REGION DESCRIPTION
	  (/**/)
	END_REGION DESCRIPTION

	REGION Extended description
	  // Function:
	  // Your position may be entered at LatitudeDD/LongitudeDD
	  // Format signed degree(decimal)
	  // 
	  // OffsetSunrise is added to Latitude and output at Sunrise
	  // OffsetSunset is added to Longitude and output at Sunset
	  // 
	  // daytime is set when the actual time is inbetween Sunrise and Sunset
	  // ============================================================================
	  // Advice:
	  // please take care that the system time is permanently synchronizes anyhow
	  // ============================================================================
	  // Abbreviations
	  // lon: Longitude
	  // lat: Latitude
	  // dir: direction (valid characters: n, N, s, S, e, E, w, W)
	  // DD: decimal degrees (type Real)
	  //=============================================================================
	  // If a faulty value is entered at LatitudeDD
	  // then the value of Sunrise is set to zero
	  // and the value of Sunset is set to zero
	  // and daytime is set to zero
	END_REGION

	REGION Block processing
	  // rising edge at enable
	  IF (#enable AND NOT #statEnableOld) AND (#statStatus = #STATUS_NO_CALL) THEN // Enable FB
	    // First call; initialize FB - set BUSY to TRUE
	    #statBusy := TRUE;
	    #statStatus := #STATUS_IN_OPERATION;
	    #statSubfunctionStatus := #NO_ERROR;

	    // falling edge at enable
	  ELSIF (NOT #enable AND #statEnableOld) THEN  // Disable FB 
	    // disbaling FB; set BUSY to FALSE
	    #statBusy := FALSE;
	    #statStatus := #STATUS_NO_CALL;
	    #statSubfunctionStatus := #NO_ERROR;

	    // just if block is in operation,
	    // otherwise jump over region -> End here to reduce "system load"
	  ELSIF (#enable AND #statEnableOld) THEN
	    REGION Process astro clock calculation
	      // reset internal status storage
	      #statStatus := #STATUS_IN_OPERATION;
	      #statSubfunctionStatus := #NO_ERROR;

	      REGION System time, local time, time zone
	        // Reading system time UTC
	        #tempRetval := INT_TO_WORD(RD_SYS_T(#tempSysTime));

	        IF (#tempRetval <> #NO_ERROR) THEN
	          #statStatus := #ERR_RD_SYS_T;
	          #statSubfunctionStatus := #tempRetval;
	        END_IF;

	        // Reading official actual local time
	        #tempRetval := INT_TO_WORD(RD_LOC_T(#tempOfficLocTime));

	        IF (#tempRetval <> #NO_ERROR) AND (#tempRetval <> #SUBSTATUS_DAYLIGHT_SAVING_TIME) THEN
	          #statStatus := #ERR_RD_LOC_T;
	          #statSubfunctionStatus := #tempRetval;
	        END_IF;

	        // Calculation of time difference #tempSysTime - #tempOfficLocTime
	        #tempTimeZone := DINT_TO_REAL(TIME_TO_DINT(T_DIFF(IN1 := #tempSysTime, IN2 := #tempOfficLocTime))) / #MS_PER_HOUR;
	      END_REGION System time, local time, time zone

	      REGION Day of the year
	        #tempDate1Jan.YEAR := #tempOfficLocTime.YEAR;
	        #tempDate1Jan.MONTH := #FIRST_MONTH_AND_DAY;
	        #tempDate1Jan.DAY := #FIRST_MONTH_AND_DAY;
	        #tempDayOfYear := UDINT_TO_REAL(DATE_TO_UDINT(DTL_TO_DATE(#tempOfficLocTime)) - DATE_TO_UDINT(DTL_TO_DATE(#tempDate1Jan)) + 1);
	      END_REGION Day of the year

	      REGION input value check
	        // input latitude DD > 90Â°, set fault
	        IF (ABS(#positionGps.latitude) > #DEGREE_90) THEN
	          #statStatus := #ERR_LATITUDE_VALUE;
	        END_IF;

	        // input longitude DD > 180Â°, set fault
	        IF (ABS(#positionGps.longitude) > #DEGREE_180) THEN
	          #statStatus := #ERR_LONGITUDE_VALUE;
	        END_IF;

	        // Copy GPS DD values to work variables and the convert to work units
	        #statLatitudeRadiant := #positionGps.latitude * #DEGREE_TO_RADIANT;
	        #statLongitudeHour := #positionGps.longitude / #DEGREE_15;
	      END_REGION input value check      

	      REGION Calculate Sunrise
	        // Aproximate time 
	        #tempAproximateTime := #tempDayOfYear + (#APPROXIMATE_TIME_6 - #statLongitudeHour) / #HOURS_PER_DAY;

	        // Sun's mean anomaly
	        #tempSunMeanAnomaly := #SUN_MEAN_ANOMALY_0_9856 * #tempAproximateTime - #SUN_MEAN_ANOMALY_3_289;
	        #tempSunMeanRad := #tempSunMeanAnomaly * #DEGREE_TO_RADIANT;

	        // Sun's true longitude
	        #tempSunLongitude := #tempSunMeanAnomaly + #SUN_TRUE_LONGITUDE_282_634
	        + #SUN_TRUE_LONGITUDE_1_916 * SIN(#tempSunMeanRad)
	        + #SUN_TRUE_LONGITUDE_0_02 * SIN(#SUN_TRUE_LONGITUDE_2_0 * #tempSunMeanRad);

	        WHILE #tempSunLongitude > #DEGREE_360 DO
	          #tempSunLongitude -= #DEGREE_360;
	        END_WHILE;

	        WHILE #tempSunLongitude < #DEGREE_0 DO
	          #tempSunLongitude += #DEGREE_360;
	        END_WHILE;

	        #tempSunLongitudeRadiant := #tempSunLongitude * #DEGREE_TO_RADIANT;

	        // Sun's right ascension
	        #tempSunAscensRad := ATAN(#SUN_RIGHT_ASCENSION_0_91764 * TAN(#tempSunLongitudeRadiant));

	        WHILE #tempSunAscensRad > #SUN_RIGHT_ASCENSION_2_PI DO
	          #tempSunAscensRad -= #SUN_RIGHT_ASCENSION_2_PI;
	        END_WHILE;

	        WHILE #tempSunAscensRad < #DEGREE_0 DO
	          #tempSunAscensRad += #SUN_RIGHT_ASCENSION_2_PI;
	        END_WHILE;

	        #tempSunAscension := #tempSunAscensRad * #RADIANT_TO_DEGREE;
	        #tempSunAscension := (#tempSunAscension
	        + DINT_TO_REAL(FLOOR(#tempSunLongitude / #DEGREE_90)) * #DEGREE_90
	        - DINT_TO_REAL(FLOOR(#tempSunAscension / #DEGREE_90)) * #DEGREE_90)
	        / #DEGREE_15;

	        // Sun's declination
	        #tempSinDeclination := #SUN_DECLINATION_0_39782 * SIN(#tempSunLongitudeRadiant);
	        #tempCosDeclination := COS(ASIN(#tempSinDeclination));

	        // Sun's local hour angle angle
	        #tempCosLocHourAngle := LREAL_TO_REAL(
	                                              (COS(#SUN_ZENIT) - REAL_TO_LREAL(#tempSinDeclination * SIN(#statLatitudeRadiant)))
	                                              / (#tempCosDeclination * COS(#statLatitudeRadiant))
	        );

	        IF (#tempCosLocHourAngle > #SUM_LOCAL_HOUR_ANGLE) THEN
	          #tempCosLocHourAngle := #SUM_LOCAL_HOUR_ANGLE;
	        ELSIF (#tempCosLocHourAngle < - #SUM_LOCAL_HOUR_ANGLE) THEN
	          #tempCosLocHourAngle := - #SUM_LOCAL_HOUR_ANGLE;
	        END_IF;

	        // Calculate local hour angle for sunrise
	        #tempLocalHourAngle := (#DEGREE_360 - ACOS(#tempCosLocHourAngle) * #RADIANT_TO_DEGREE) / #DEGREE_15;

	        // Local mean time of rising/setting
	        #tempLocalMeanTime := #tempLocalHourAngle + #tempSunAscension - (#LOCAL_MEAN_TIME_0_06571 * #tempAproximateTime) - #LOCAL_MEAN_TIME_6_622;

	        // Adjust back TO UTC
	        #tempUTC := #tempLocalMeanTime - #statLongitudeHour;

	        IF (#tempUTC > #HOURS_PER_DAY) THEN
	          #tempUTC -= #HOURS_PER_DAY;
	        ELSIF (#tempUTC < 0) THEN
	          #tempUTC += #HOURS_PER_DAY;
	        END_IF;

	        #tempLocalTime := #tempUTC - #tempTimeZone;

	        IF (#tempLocalTime >= #HOURS_PER_DAY) THEN
	          #tempLocalTime -= DINT_TO_REAL(TRUNC(#tempLocalTime / #HOURS_PER_DAY)) * #HOURS_PER_DAY;
	        ELSIF (#tempLocalTime < 0) THEN
	          #tempLocalTime += #HOURS_PER_DAY;
	        END_IF;

	        // Convert #tempLocalTime to DTL (#tempIntSunrise) and add offset
	        #tempSunrise.YEAR := #tempOfficLocTime.YEAR;
	        #tempSunrise.MONTH := #tempOfficLocTime.MONTH;
	        #tempSunrise.DAY := #tempOfficLocTime.DAY;
	        #tempSunrise := #tempSunrise + UDINT_TO_TIME(REAL_TO_UDINT(#tempLocalTime * #MS_PER_HOUR));
	        #tempSunrise.NANOSECOND := 0;
	        #tempSunrise += #offsetSunrise;
	      END_REGION Calculate Sunrise

	      REGION Calculate Sunset
	        // Aproximate time 
	        #tempAproximateTime := #tempDayOfYear + (#APPROXIMATE_TIME_18 - #statLongitudeHour) / #HOURS_PER_DAY;

	        // Sun's mean anomaly
	        #tempSunMeanAnomaly := #SUN_MEAN_ANOMALY_0_9856 * #tempAproximateTime - #SUN_MEAN_ANOMALY_3_289;
	        #tempSunMeanRad := #tempSunMeanAnomaly * #DEGREE_TO_RADIANT;

	        // Sun's true longitude
	        #tempSunLongitude := #tempSunMeanAnomaly + #SUN_TRUE_LONGITUDE_282_634
	        + #SUN_TRUE_LONGITUDE_1_916 * SIN(#tempSunMeanRad)
	        + #SUN_TRUE_LONGITUDE_0_02 * SIN(#SUN_TRUE_LONGITUDE_2_0 * #tempSunMeanRad);

	        WHILE #tempSunLongitude > #DEGREE_360 DO
	          #tempSunLongitude -= #DEGREE_360;
	        END_WHILE;

	        WHILE #tempSunLongitude < #DEGREE_0 DO
	          #tempSunLongitude += #DEGREE_360;
	        END_WHILE;

	        #tempSunLongitudeRadiant := #tempSunLongitude * #DEGREE_TO_RADIANT;

	        // Sun's right ascension
	        #tempSunAscensRad := ATAN(#SUN_RIGHT_ASCENSION_0_91764 * TAN(#tempSunLongitudeRadiant));

	        WHILE #tempSunAscensRad > #SUN_RIGHT_ASCENSION_2_PI DO
	          #tempSunAscensRad -= #SUN_RIGHT_ASCENSION_2_PI;
	        END_WHILE;

	        WHILE #tempSunAscensRad < #DEGREE_0 DO
	          #tempSunAscensRad += #SUN_RIGHT_ASCENSION_2_PI;
	        END_WHILE;

	        #tempSunAscension := #tempSunAscensRad * #RADIANT_TO_DEGREE;
	        #tempSunAscension := (#tempSunAscension
	        + DINT_TO_REAL(FLOOR(#tempSunLongitude / #DEGREE_90)) * #DEGREE_90
	        - DINT_TO_REAL(FLOOR(#tempSunAscension / #DEGREE_90)) * #DEGREE_90)
	        / #DEGREE_15;

	        // Sun's declination
	        #tempSinDeclination := #SUN_DECLINATION_0_39782 * SIN(#tempSunLongitudeRadiant);
	        #tempCosDeclination := COS(ASIN(#tempSinDeclination));

	        // Sun's local hour angle
	        #tempCosLocHourAngle := LREAL_TO_REAL(
	                                              (COS(#SUN_ZENIT) - REAL_TO_LREAL(#tempSinDeclination * SIN(#statLatitudeRadiant)))
	                                              / (#tempCosDeclination * COS(#statLatitudeRadiant))
	        );

	        IF (#tempCosLocHourAngle > #SUM_LOCAL_HOUR_ANGLE) THEN
	          #tempCosLocHourAngle := #SUM_LOCAL_HOUR_ANGLE;
	        ELSIF (#tempCosLocHourAngle < - #SUM_LOCAL_HOUR_ANGLE) THEN
	          #tempCosLocHourAngle := - #SUM_LOCAL_HOUR_ANGLE;
	        END_IF;

	        // Calculation local hour angle for sunset
	        #tempLocalHourAngle := ACOS(#tempCosLocHourAngle) * #RADIANT_TO_DEGREE / #DEGREE_15;

	        // Local mean time of rising/setting
	        #tempLocalMeanTime := #tempLocalHourAngle + #tempSunAscension - (#LOCAL_MEAN_TIME_0_06571 * #tempAproximateTime) - #LOCAL_MEAN_TIME_6_622;

	        // Adjust back TO UTC
	        #tempUTC := #tempLocalMeanTime - #statLongitudeHour;

	        IF (#tempUTC > #HOURS_PER_DAY) THEN
	          #tempUTC -= #HOURS_PER_DAY;
	        ELSIF (#tempUTC < 0) THEN
	          #tempUTC += #HOURS_PER_DAY;
	        END_IF;

	        #tempLocalTime := #tempUTC - #tempTimeZone;

	        IF (#tempLocalTime >= #HOURS_PER_DAY) THEN
	          #tempLocalTime -= DINT_TO_REAL(TRUNC(#tempLocalTime / #HOURS_PER_DAY)) * #HOURS_PER_DAY;
	        ELSIF (#tempLocalTime < 0) THEN
	          #tempLocalTime += #HOURS_PER_DAY;
	        END_IF;

	        // Convert #tempLocalTime to DTL (#tempIntSunset) and add offset
	        #tempSunset.YEAR := #tempOfficLocTime.YEAR;
	        #tempSunset.MONTH := #tempOfficLocTime.MONTH;
	        #tempSunset.DAY := #tempOfficLocTime.DAY;
	        #tempSunset := #tempSunset + UDINT_TO_TIME(REAL_TO_UDINT(#tempLocalTime * #MS_PER_HOUR));
	        #tempSunset.NANOSECOND := 0;
	        #tempSunset += #offsetSunset;
	      END_REGION Calculate Sunset

	    END_REGION Process astro clock calculation
	  END_IF;

	  // Edge detection store 'enable' input to memory
	  #statEnableOld := #enable;
	END_REGION Block processing

	REGION OUTPUTS
	  // Write application specific values to outputs
	  #actualLocalTime := #tempOfficLocTime;
	  #sunriseTime := #tempSunrise;
	  #sunsetTime := #tempSunset;
	  // Evaluate Day (between Sunrise and Sunset)
	  #isDaytime := (#tempOfficLocTime >= #tempSunrise) AND (#tempOfficLocTime <= #tempSunset);

	  // Write static values to outputs
	  #valid := NOT #statStatus.%X15 AND #statBusy;
	  #busy := #statBusy;
	  #error := #statStatus.%X15;
	  #status := #statStatus;
	  #subFunctionStatus := #statSubfunctionStatus;

	  // ENO is not used, forced to TRUE
	  ENO := TRUE;
	END_REGION OUTPUTS

END_FUNCTION_BLOCK

FUNCTION "LGF_FahrenheitToKelvin" : Real
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : Siemens_Digital_Industry_Support
FAMILY : LGF
NAME : LGF_FahrenheitToKelvin
   VAR_INPUT 
      value : Real;
   END_VAR

   VAR_TEMP 
      tempKelvinTemperature : Real;
   END_VAR

   VAR CONSTANT 
      TEMPERATURE_GAIN : Real := 1.8;
      TEMPERATURE_OFFSET_FAHRENHEIT : Real := 273.15;
      TEMPERATURE_OFFSET : Real := 32.0;
   END_VAR


BEGIN
	REGION BLOCK INFO HEADER
	  //===============================================================================
	  // SIEMENS AG / (c)Copyright 2019
	  //-------------------------------------------------------------------------------
	  // Title:            LGF_FahrenheitToKelvin
	  // Comment/Function: This function converts the Fahrenheit to Kelvin temperature
	  // Library/Family:   LGF(Library General Functions)
	  // Author:           Siemens Digital Industry Support
	  // Tested with:      S7-PLCSIM Advanced V2.0 SP1
	  // Engineering:      TIA Portal V15.1
	  // Restrictions:     ENO disabled - no error handling
	  // Requirements:     PLC (S7-1200 / S7-1500)
	  //-------------------------------------------------------------------------------
	  // Change log table:
	  // Version  | Date       | Expert in charge       | Changes applied
	  //----------|------------|------------------------|------------------------------
	  // 01.00.00 | 20.06.2019 | Simatic Systems Support
	  //                         First release
	  // 03.00.00 | 23.04.2020 | Simatic Systems Support | Set version to V3.0.0
	  //                         harmonize the version of the whole library
	  // 03.00.01 | 22.02.2021 | Simatic Systems Support
	  //                         Insert documentation
	  //===============================================================================
	END_REGION

	REGION CONVERT
	  #tempKelvinTemperature := ((#value - #TEMPERATURE_OFFSET) / #TEMPERATURE_GAIN) + #TEMPERATURE_OFFSET_FAHRENHEIT;
	END_REGION

	REGION OUTPUTS
	  #LGF_FahrenheitToKelvin := #tempKelvinTemperature;
	  // no error handling needed
	  ENO := TRUE;
	END_REGION

END_FUNCTION

FUNCTION_BLOCK "LGF_ShiftRegister"
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : Siemens_Digital_Industry_Support
FAMILY : LGF
NAME : LGF_ShiftRegister
   VAR_INPUT 
      enable : Bool;
      shiftLeft : Bool;
      shiftRight : Bool;
      shiftRange { S7_PredefinedAssignment := '1'; S7_HiddenAssignment := 'HideIfNoParamAssigned'} : UInt := 1;
      reset : Bool;
      clear : Bool;
      fill : Bool;
      initialItem { S7_PredefinedAssignment := 'NULL'} : Variant;
   END_VAR

   VAR_OUTPUT 
      valid { ExternalWritable := 'False'} : Bool;
      busy { ExternalWritable := 'False'} : Bool;
      error { ExternalWritable := 'False'} : Bool;
      status { ExternalWritable := 'False'} : Word;
      leftShiftCount { ExternalWritable := 'False'} : DInt;
      rightShiftCount { ExternalWritable := 'False'} : DInt;
      shiftOffsetCounter { ExternalWritable := 'False'} : DInt;
      diagnostics { ExternalWritable := 'False'} : "LGF_typeDiagnostics";
   END_VAR

   VAR_IN_OUT 
      bufferRegister : Variant;
   END_VAR

   VAR 
      statInternal { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Struct
         enablePrevious { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         valid { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         busy { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         error { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         errorUserCleared { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         errorAutoCleared { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         disablingCompleted { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         diagnostics { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : "LGF_typeDiagnostics";
         emptyDiagnostics { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : "LGF_typeDiagnostics";
      END_STRUCT;
      statEdgesMem { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Struct
         shiftRight { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         shiftLeft { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         reset { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         clear { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
      END_STRUCT;
      statBufferSize { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : UDInt;
      statLeftShiftCount { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : DInt := 0;
      statRightShiftCount { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : DInt := 0;
      statMainState { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : DInt := #FB_STATE_NO_PROCESSING;
   END_VAR

   VAR_TEMP 
      tempEnable : Bool;
      tempEdges : Struct
         shiftLeft : Bool;
         shiftRight : Bool;
         reset : Bool;
         clear : Bool;
      END_STRUCT;
      tempInternalError : Int;
      tempCounter : DInt;
      tempRepeatStateMaschine : Bool;
   END_VAR

   VAR CONSTANT 
      NO_INTERNAL_ERROR : Int := 16#0000;
      EMPTY_INITIALIZED : Int := 0;
      INDEX_BEGINNING : Int := 0;
      COUNT_ELEMENTS : UDInt := 1;
      INCREMENT : Int := 1;
      BUFFER_SIZE_CORRECTION : UDInt := 1;
      COUNTER_LOWER_LIMIT : DInt := 0;
      ZERO_ELEMENTS : DInt := 0;
      FB_STATE_NO_PROCESSING : DInt := 0;
      FB_STATE_ENABLING : DInt := 1;
      FB_STATE_IDLE : DInt := 10;
      FB_STATE_RESET : DInt := 11;
      FB_STATE_CLEAR : DInt := 12;
      FB_STATE_SHIFT_LEFT : DInt := 20;
      FB_STATE_SHIFT_RIGHT : DInt := 30;
      FB_STATE_DISABLING : DInt := 90;
      STATUS_NO_ERROR : Word := 16#0000;
      STATUS_NO_CURRENT_JOBS : Word := 16#7000;
      STATUS_FIRST_CALL : Word := 16#7001;
      STATUS_SUBSEQUENT_CALL : Word := 16#7002;
      SUB_STATUS_NO_ERROR : Word := 16#0000;
      ERR_BUFFER_FULL : Word := 16#8002;
      ERR_BUFFER_EMPTY : Word := 16#8001;
      ERR_NO_ARRAY : Word := 16#8200;
      ERR_CLEARING_WITHOUT_INITIAL_ITEM : Word := 16#8201;
      ERR_FILL_WITHOUT_INITIAL_ITEM : Word := 16#8202;
      ERR_WRONG_TYPE_INITIAL_ITEM : Word := 16#8203;
      ERR_BOOL_NOT_SUPPORTED : Word := 16#8204;
      ERR_MULTIPLE_COMMANDS_DEDECTED : Word := 16#8401;
      ERR_IN_SHIFT_RANGE : Word := 16#8402;
      ERR_UNDEFINED_STATE : Word := 16#8600;
      ERR_CLEAR_BUFFER : Word := 16#8610;
      ERR_SHIFT_BUFFER_LEFT : Word := 16#8611;
      ERR_SHIFT_BUFFER_LEFT_FILL : Word := 16#8612;
      ERR_SHIFT_BUFFER_RIGHT : Word := 16#8621;
      ERR_SHIFT_BUFFER_RIGHT_FILL : Word := 16#8622;
   END_VAR


BEGIN
	REGION Block info header
	  //===============================================================================
	  // SIEMENS AG / (c)Copyright 2019
	  //-------------------------------------------------------------------------------
	  // Title:            LGF_ShiftRegister
	  // Comment/Function: The Function represnts a shift register for any kind of Datatype (using `variant`) - it can be used as a kind of a ring buffer.
	  //                   It is possible to shift the register array left (index `array[n]:=array[n+1]`) or right (index a`array[n]:=array[n-1]`).
	  //                   As this is a real shift operation, it may causes some runtime effects while using big array sizes to move.
	  //                   It could be used for pusing the material traking UDT trouf the maschin, e.g. a rotary indexing table
	  //                   Please consider that a FIFO  or LIFO storage, based on indexes, could be used as well for most applications.
	  // Library/Family:   LGF (Library General Functions)
	  // Author:           Siemens Digital Industry Support
	  // Tested with:      CPU 1515F-2 PN FW:V2.6
	  // Engineering:      TIA Portal V16 Update 4
	  // Restrictions:     ENO disabled - error handling done with error and status
	  //                   Bool array is not supported by `MOVE_BLK_VARIANT`
	  // Requirements:     PLC (S7-1200 / S7-1500)
	  //-------------------------------------------------------------------------------
	  // Change log table:
	  // Version  | Date       | Expert in charge       | Changes applied
	  //----------|------------|------------------------|------------------------------
	  // 01.00.00 03.07.2018  Siemens Industry Online Support
	  //                      First released version
	  // 03.00.00 09.04.2021  Simatic Systems Support
	  //                      Refactoring and alignment to Datatype Variant
	  //                      Insert documentation
	  // 04.00.00 04.09.2024  Simatic Systems Support
	  //                      Rework to PLC Open `Enable` behavior
	  //                      Add `reset` input / counter outputs
	  //                      Fix Bug while filling after left shift operation
	  //                      Rework to diagnostic output datatype
	  //=============================================================================
	END_REGION Block info header

	REGION DESCRIPTION
	  (/**/)
	END_REGION DESCRIPTION


	REGION ENABLING/DISABLING
	  #tempEnable := #enable; // Work with temporary value / create process image
	  IF (#tempEnable = TRUE) AND (#statInternal.diagnostics.status = #STATUS_NO_CURRENT_JOBS) THEN // Enable FB
	    // First call; initialize FB
	    #statInternal.valid := TRUE;
	    #statInternal.busy := TRUE;
	    #statInternal.error := FALSE;
	    #statInternal.errorUserCleared := FALSE;
	    #statInternal.errorAutoCleared := FALSE;
	    #statInternal.disablingCompleted := FALSE;
	    #statInternal.diagnostics := #statInternal.emptyDiagnostics;
	    #diagnostics := #statInternal.emptyDiagnostics;
	    #statInternal.diagnostics.status := #STATUS_FIRST_CALL;
	    // State machine - start processing
	    #statMainState := #FB_STATE_ENABLING;

	  ELSIF (#tempEnable = FALSE) AND (#statInternal.enablePrevious = TRUE) THEN // Disable FB 
	    #statMainState := #FB_STATE_DISABLING;

	  ELSIF (#statInternal.diagnostics.status = #STATUS_FIRST_CALL) THEN // Set status identifier of subsequent call
	    #statInternal.diagnostics.status := #STATUS_SUBSEQUENT_CALL;
	  END_IF;

	  // Edge detection 'enable' input
	  #statInternal.enablePrevious := #tempEnable;
	END_REGION ENABLING/DISABLING

	IF (#statInternal.diagnostics.status = #STATUS_NO_CURRENT_JOBS) THEN // Nothing to do -> End here to reduce "system load"
	  RETURN;
	END_IF;

	REGION STATE MACHINE
	  REPEAT
	    #tempRepeatStateMaschine := FALSE;

	    CASE #statMainState OF // State machine of FB
	      #FB_STATE_NO_PROCESSING:
	        REGION No Processing
	          ; // No processing active (Note: this state must always be present and left empty)
	        END_REGION No Processing

	      #FB_STATE_ENABLING: // Enabling active
	        REGION Enabling
	          // check whether the item type is BOOL
	          // Bool is not supported by Systemfunction MOVE BLOCK VARIANT
	          IF FALSE
	            OR TypeOf(#initialItem) = Bool
	            OR TypeOfElements(#bufferRegister) = Bool
	          THEN
	            #statInternal.errorUserCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_BOOL_NOT_SUPPORTED;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_NO_ERROR;
	            EXIT;
	          END_IF;
	          // check whether the ring #buffer is an ARRAY.
	          // IF so, the number OF the ARRAY elements is read out.
	          // IF it is NOT an ARRAY, the program execution is terminated at this point
	          IF IS_ARRAY(#bufferRegister) THEN
	            #statBufferSize := CountOfElements(#bufferRegister);
	          ELSE
	            #statInternal.errorUserCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_NO_ARRAY;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_NO_ERROR;
	            EXIT;
	          END_IF;

	          // check whether the initial value OF the ring #buffer
	          // matches the entry (tag #item). IF the data types DO NOT match,
	          // the program execution is terminated at this point
	          IF TRUE
	            AND (#initialItem <> NULL)
	            AND (TypeOfElements(#bufferRegister) <> TypeOf(#initialItem))
	          THEN
	            #statInternal.errorUserCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_WRONG_TYPE_INITIAL_ITEM;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_NO_ERROR;
	            EXIT;
	          END_IF;

	          // Store values for edge detection
	          #statEdgesMem.shiftLeft := #shiftLeft;
	          #statEdgesMem.shiftRight := #shiftRight;
	          #statEdgesMem.reset := #reset;
	          #statEdgesMem.clear := #clear;

	          #statMainState := #FB_STATE_IDLE;
	          #tempRepeatStateMaschine := TRUE;
	        END_REGION Enabling

	      #FB_STATE_IDLE:
	        REGION IDLE
	          // Collect edges
	          #tempEdges.shiftRight := #shiftRight AND NOT #statEdgesMem.shiftRight;
	          #tempEdges.shiftLeft := #shiftLeft AND NOT #statEdgesMem.shiftLeft;
	          #tempEdges.reset := #reset AND NOT #statEdgesMem.reset;
	          #tempEdges.clear := #clear AND NOT #statEdgesMem.clear;
	          // Store values for edge detection
	          #statEdgesMem.shiftRight := #shiftRight;
	          #statEdgesMem.shiftLeft := #shiftLeft;
	          #statEdgesMem.reset := #reset;
	          #statEdgesMem.clear := #clear;

	          // Reset is allways possible
	          IF #tempEdges.reset THEN
	            #statMainState := #FB_STATE_RESET;
	            #tempRepeatStateMaschine := TRUE;

	            // If error Pending - do nothing until error was reseted
	          ELSIF #statInternal.diagnostics.status.%X15 THEN
	            ;// Stay here and no further operation possible

	            // Check if number of movements is in a plausible and valid range
	            // -> Has to be lees than the buffer size
	          ELSIF #shiftRange >= #statBufferSize THEN
	            #statInternal.errorUserCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_IN_SHIFT_RANGE;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_NO_ERROR;

	            // If Fill is activated, an inital item has to be present
	          ELSIF (#fill AND #initialItem = NULL) THEN
	            #statInternal.errorUserCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_FILL_WITHOUT_INITIAL_ITEM;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_NO_ERROR;

	            // If clearBuffer has rising edge, the buffer is initialized by initial values
	            // Check whether the initial value OF the ring #buffer
	            // matches the entry (tag #item). IF the data types DO NOT match,
	            // the program execution is terminated at this point
	          ELSIF (#tempEdges.clear AND #initialItem = NULL) THEN
	            #statInternal.errorAutoCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_CLEARING_WITHOUT_INITIAL_ITEM;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_NO_ERROR;

	            // If Fill is activated, an inital item has to be present
	          ELSIF (#fill AND #initialItem = NULL) THEN
	            #statInternal.errorAutoCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_FILL_WITHOUT_INITIAL_ITEM;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_NO_ERROR;

	            // Multiple commands dedected
	          ELSIF (BOOL_TO_INT(#tempEdges.clear) + BOOL_TO_INT(#tempEdges.shiftRight) + BOOL_TO_INT(#tempEdges.shiftLeft)) > 1 THEN
	            #statInternal.errorAutoCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_MULTIPLE_COMMANDS_DEDECTED;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_NO_ERROR;

	            // Commands
	          ELSIF #tempEdges.clear THEN
	            #statMainState := #FB_STATE_CLEAR;
	            #tempRepeatStateMaschine := TRUE;
	          ELSIF #tempEdges.shiftRight THEN
	            #statMainState := #FB_STATE_SHIFT_RIGHT;
	            #tempRepeatStateMaschine := TRUE;
	          ELSIF #tempEdges.shiftLeft THEN
	            #statMainState := #FB_STATE_SHIFT_LEFT;
	            #tempRepeatStateMaschine := TRUE;
	          ELSE
	            #statInternal.diagnostics.status := #STATUS_SUBSEQUENT_CALL;
	          END_IF;
	        END_REGION IDLE

	      #FB_STATE_CLEAR:
	        REGION CLEARING
	          // Reset status
	          #statInternal.errorAutoCleared := FALSE;

	          FOR #tempCounter := #COUNTER_LOWER_LIMIT TO UDINT_TO_DINT(#statBufferSize - #BUFFER_SIZE_CORRECTION) DO
	            #tempInternalError := MOVE_BLK_VARIANT(SRC := #initialItem,
	                                                   COUNT := #COUNT_ELEMENTS,
	                                                   SRC_INDEX := #INDEX_BEGINNING,
	                                                   DEST_INDEX := #tempCounter,
	                                                   DEST => #bufferRegister);

	            // Checks whether a local error has occurred.
	            IF (#tempInternalError <> #NO_INTERNAL_ERROR) THEN
	              #statInternal.errorAutoCleared := TRUE;
	              #statInternal.diagnostics.status := #ERR_CLEAR_BUFFER;
	              #statInternal.diagnostics.subfunctionStatus := INT_TO_WORD(#tempInternalError);
	              EXIT;
	            END_IF;
	          END_FOR;
	          IF #statInternal.errorAutoCleared THEN
	            EXIT;
	          END_IF;

	          // Reset counters
	          #statLeftShiftCount :=
	          #statRightShiftCount := #EMPTY_INITIALIZED;
	          // Set next state
	          #statMainState := #FB_STATE_IDLE;
	          #tempRepeatStateMaschine := TRUE;
	        END_REGION

	      #FB_STATE_RESET:
	        REGION RESET
	          // Reset Error Auto Cleared
	          #statInternal.errorAutoCleared := FALSE;
	          // Set next state
	          #statMainState := #FB_STATE_IDLE;
	          #tempRepeatStateMaschine := TRUE;
	        END_REGION

	      #FB_STATE_SHIFT_LEFT:
	        REGION SHIFT_LEFT
	          // Shift whole array elemnts from N --> N-1
	          #tempInternalError := MOVE_BLK_VARIANT(SRC := #bufferRegister,
	                                                 COUNT := #statBufferSize - #shiftRange,
	                                                 SRC_INDEX := #shiftRange,
	                                                 DEST_INDEX := 0,
	                                                 DEST => #bufferRegister);

	          // Check whether a local error has occurred
	          IF (#tempInternalError <> #NO_INTERNAL_ERROR) THEN
	            #statInternal.errorAutoCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_SHIFT_BUFFER_LEFT;
	            #statInternal.diagnostics.subfunctionStatus := INT_TO_WORD(#tempInternalError);
	            EXIT;
	          END_IF;

	          REGION Fill after shift left
	            IF #fill THEN
	              // Run through buffer and overwrite all elements with its initial item element
	              FOR #tempCounter := UDINT_TO_DINT(#statBufferSize - #shiftRange) TO UDINT_TO_DINT(#statBufferSize - #BUFFER_SIZE_CORRECTION) DO
	                // Overwrite the "old" values after shift
	                #tempInternalError := MOVE_BLK_VARIANT(SRC := #initialItem,
	                                                       COUNT := #COUNT_ELEMENTS,
	                                                       SRC_INDEX := #INDEX_BEGINNING,
	                                                       DEST_INDEX := #tempCounter,
	                                                       DEST => #bufferRegister);

	                // Check whether a local error has occurred
	                IF (#tempInternalError <> #NO_INTERNAL_ERROR) THEN
	                  #statInternal.errorAutoCleared := TRUE;
	                  #statInternal.diagnostics.status := #ERR_SHIFT_BUFFER_LEFT_FILL;
	                  #statInternal.diagnostics.subfunctionStatus := INT_TO_WORD(#tempInternalError);
	                  EXIT;
	                END_IF;
	              END_FOR;
	              IF #statInternal.errorAutoCleared THEN
	                EXIT;
	              END_IF;
	            END_IF;
	          END_REGION Fill after shift left

	          // This program code section increments the index by 1 for shifting.    
	          #statLeftShiftCount += #INCREMENT;
	          // Set next state
	          #statMainState := #FB_STATE_IDLE;
	          #tempRepeatStateMaschine := TRUE;
	        END_REGION

	      #FB_STATE_SHIFT_RIGHT:
	        REGION SHIFT_RIGHT
	          // Shift whole array elemnsts from N --> N+1
	          #tempInternalError := MOVE_BLK_VARIANT(SRC := #bufferRegister,
	                                                 COUNT := #statBufferSize - #shiftRange,
	                                                 SRC_INDEX := #INDEX_BEGINNING,
	                                                 DEST_INDEX := #shiftRange,
	                                                 DEST => #bufferRegister);

	          // Check whether a local error has occurred
	          IF (#tempInternalError <> #NO_INTERNAL_ERROR) THEN
	            #statInternal.errorAutoCleared := TRUE;
	            #statInternal.diagnostics.status := #ERR_SHIFT_BUFFER_RIGHT;
	            #statInternal.diagnostics.subfunctionStatus := INT_TO_WORD(#tempInternalError);
	            RETURN;
	          END_IF;

	          REGION Fill after shift right
	            IF #fill THEN
	              // Run through buffer and overwrite all elements with its initial item element
	              FOR #tempCounter := #INDEX_BEGINNING TO UDINT_TO_DINT(#shiftRange - #BUFFER_SIZE_CORRECTION) DO
	                // Overwrite the "old" values after shift
	                #tempInternalError := MOVE_BLK_VARIANT(SRC := #initialItem,
	                                                       COUNT := #COUNT_ELEMENTS,
	                                                       SRC_INDEX := #INDEX_BEGINNING,
	                                                       DEST_INDEX := #tempCounter,
	                                                       DEST => #bufferRegister);

	                // Check whether a local error has occurred
	                IF (#tempInternalError <> #NO_INTERNAL_ERROR) THEN
	                  #statInternal.errorAutoCleared := TRUE;
	                  #statInternal.diagnostics.status := #ERR_SHIFT_BUFFER_RIGHT_FILL;
	                  #statInternal.diagnostics.subfunctionStatus := INT_TO_WORD(#tempInternalError);
	                  EXIT;
	                END_IF;
	              END_FOR;
	              IF #statInternal.errorAutoCleared THEN
	                EXIT;
	              END_IF;
	            END_IF;
	          END_REGION Fill after shift right

	          // This program code section increments the index by 1 for shifting.    
	          #statRightShiftCount += #INCREMENT;
	          // Set next state
	          #statMainState := #FB_STATE_IDLE;
	          #tempRepeatStateMaschine := TRUE;
	        END_REGION

	      #FB_STATE_DISABLING: // Disabling active
	        REGION Disabling
	          // Disabling is complete the bit #statDisablingCompleted must be set
	          #statInternal.disablingCompleted := TRUE;
	        END_REGION Disabling

	      ELSE // Undefined state in state machine reached
	        REGION Undefined state
	          #statInternal.diagnostics.status := #ERR_UNDEFINED_STATE;
	          #statInternal.diagnostics.subfunctionStatus := 16#00;
	          #statInternal.diagnostics.stateNumber := #statMainState;
	          #statInternal.errorUserCleared := TRUE;
	        END_REGION Undefined state
	    END_CASE;
	  UNTIL #tempRepeatStateMaschine = false END_REPEAT;
	END_REGION STATE MACHINE

	REGION OUTPUTS
	  // Write outputs
	  IF (#statInternal.disablingCompleted = TRUE) THEN
	    REGION Disabling
	      // Reset outputs if disabling completed
	      #statInternal.valid := FALSE;
	      #statInternal.busy := FALSE;
	      #statInternal.error := FALSE;
	      #statInternal.errorUserCleared := FALSE;
	      #statInternal.errorAutoCleared := FALSE;
	      #statInternal.diagnostics.status := #STATUS_NO_CURRENT_JOBS;
	      #statMainState := #FB_STATE_NO_PROCESSING;
	    END_REGION Disabling

	  ELSIF (#statInternal.errorUserCleared = TRUE) AND (#statInternal.error = FALSE) THEN
	    REGION Error user cleared
	      // Error can only be cleared by user; rising edge at enable input is needed to continue 
	      #statInternal.valid := FALSE;
	      #statInternal.busy := FALSE;
	      #statInternal.error := TRUE;
	      // Write diagnostics
	      #diagnostics := #statInternal.diagnostics;
	      // Execution aborted --> set state no processing
	      #statMainState := #FB_STATE_NO_PROCESSING;
	    END_REGION Error user cleared

	  ELSIF (#statInternal.errorAutoCleared = TRUE) AND (#statInternal.error = FALSE) THEN
	    REGION Error auto cleared
	      // Error can be reset by FB automatically
	      #statInternal.valid := FALSE;
	      #statInternal.busy := TRUE;
	      #statInternal.error := TRUE;
	      // Write diagnostics
	      #diagnostics := #statInternal.diagnostics;
	      // Handling of autocleared error
	      #statMainState := #FB_STATE_IDLE;
	      #tempRepeatStateMaschine := TRUE;
	    END_REGION Error auto cleared

	  ELSIF (#statInternal.errorAutoCleared = FALSE) AND (#statInternal.errorUserCleared = FALSE) AND (#statInternal.error = TRUE) THEN
	    REGION After user/auto cleared or command aborted
	      // If autocleared error is acknowledged
	      #statInternal.valid := TRUE;
	      #statInternal.busy := TRUE;
	      #statInternal.error := FALSE;
	      #statInternal.diagnostics.status := #STATUS_SUBSEQUENT_CALL;
	    END_REGION After user/auto cleared or command aborted
	  END_IF;

	  REGION Write static values to outputs
	    #valid := #statInternal.valid;
	    #busy := #statInternal.busy;
	    #error := #statInternal.error;
	    #status := #statInternal.diagnostics.status;
	    // if ENO mechanism is not used / error /& Status output present
	    ENO := TRUE;
	    // Write application specific static values to outputs
	    #leftShiftCount := #statLeftShiftCount;
	    #rightShiftCount := #statRightShiftCount;
	    #shiftOffsetCounter := #rightShiftCount - #leftShiftCount;
	  END_REGION Write static values to outputs
	END_REGION OUTPUTS

END_FUNCTION_BLOCK

FUNCTION "LGF_GetCalendarWeek_US" : DInt
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : Siemens_Digital_Industry
FAMILY : LGF
NAME : LGF_GetCalendarWeek_US
   VAR_INPUT 
      "date" {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
   END_VAR

   VAR_OUTPUT 
      calendarDay : DInt;
      error : Bool;
      status : Word;
   END_VAR

   VAR_TEMP 
      tempDate {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
      tempCurrentYearWeekday1Jan : UDInt;
      tempNextYearWeekday1Jan : UDInt;
      tempCurrentYearWeekday31Dec : UDInt;
      tempPrevYearWeekday1Jan : UDInt;
      tempPrevYearWeekday31Dec : UDInt;
      tempDaysCorr : DInt;
      tempWeek : DInt;
      tempDays : DInt;
      tempCurrentYearIsLeapyear : Bool;
      tempPrevYearIsLeapyear : Bool;
      tempYearCurrentYear : UInt;
      tempYearPrevYear : UInt;
      tempYearNextYear : UInt;
      tempIsCurrentYearWith53Weeks : Bool;
      tempIsPrevYearWith53Weeks : Bool;
   END_VAR

   VAR CONSTANT 
      SUNDAY : UDInt := 1;
      MONDAY : UDInt := 2;
      TUESDAY : UDInt := 3;
      WEDNESDAY : UDInt := 4;
      THURSDAY : UDInt := 5;
      FRIDAY : UDInt := 6;
      SATURDAY : UDInt := 7;
      FIRST_DAY : USInt := 1;
      LAST_DAY : USInt := 31;
      JANUARY : USInt := 1;
      DECEMBER : USInt := 12;
      ONE_YEAR : UInt := 1;
      ONE_DAY : DInt := 1;
      TWO_DAYS : DInt := 2;
      THREE_DAYS : DInt := 3;
      FOUR_DAYS : DInt := 4;
      FIVE_DAYS : DInt := 5;
      SEVEN_DAYS : DInt := 7;
      ONE_WEEK : DInt := 1;
      WEEK_1 : DInt := 1;
      WEEK_52 : DInt := 52;
      WEEK_53 : DInt := 53;
      WEEK_54 : DInt := 54;
      LEAP_YEAR_CHECK_4 : UInt := 4;
      LEAP_YEAR_CHECK_100 : UInt := 100;
      LEAP_YEAR_CHECK_400 : UInt := 400;
      STATUS_NO_ERROR : Word := 16#0000;
      ERR_LIM_DATE : Word := 16#8201;
   END_VAR


BEGIN
	REGION BLOCK INFO HEADER
	  //===============================================================================
	  // SIEMENS AG / (c)Copyright 2019
	  //-------------------------------------------------------------------------------
	  // Title:            LGF_GetCalendarWeek_US
	  // Comment/Function: This function calculates the week number for USA and other countries.
	  //                   - new week starts with Sunday
	  //                   - the week wich contains the 1st January is always week number 1
	  //                   - a year has 52 or 53 weeks
	  // Library/Family:   LGF(Library General Functions)
	  // Author:           Siemens Digital Industry Support
	  // Tested with:      S7-PLCSIM Advanced V2.0 SP1
	  // Engineering:      TIA Portal V14 Upd 1
	  // Restrictions:     ENO disabled - error handling done with error and status
	  // Requirements:     PLC (S7-1200 / S7-1500)
	  //-------------------------------------------------------------------------------
	  // Change log table:
	  // Version  | Date       | Expert in charge       | Changes applied
	  //----------|------------|------------------------|------------------------------
	  // 01.00.00 13.11.2019  Simatic Systems Support
	  //                      First release
	  //                      based on spli from  LGF_CalenderWeek (previously LGF_CalenderWeek_ISO)
	  // 03.00.00 23.04.2020  Simatic Systems Support
	  //                      Set version to V3.0.0, harmonize the version of the whole library
	  // 03.00.01 19.01.2020  Simatic Systems Support
	  //                      Insert documentation
	  //=============================================================================
	END_REGION BLOCK INFO HEADER

	REGION DESCRIPTION
	  (/**/)
	END_REGION DESCRIPTION

	REGION INITIALISATION
	  #tempDays := 0;
	  #tempWeek := 0;
	END_REGION

	REGION COUNT DAYS
	  // Days passed in the year since 1st January, "date" included
	  #tempDays := "LGF_GetCalendarDay"("date" := #date, error => #error, status => #status);
	  IF #error THEN
	    #LGF_GetCalendarWeek_US := #tempWeek;
	    RETURN;
	  END_IF;
	END_REGION

	REGION COUNT WEEKS
	  // Year of actual year, previous year and next year
	  #tempYearCurrentYear := #date.YEAR;
	  #tempYearPrevYear := #date.YEAR - #ONE_YEAR;
	  #tempYearNextYear := #date.YEAR + #ONE_YEAR;

	  // Check if current year is a leap year (29. February/366 days)
	  #tempCurrentYearIsLeapyear := (((#tempYearCurrentYear MOD #LEAP_YEAR_CHECK_4 = 0) AND NOT (#tempYearCurrentYear MOD #LEAP_YEAR_CHECK_100 = 0)) OR (#tempYearCurrentYear MOD #LEAP_YEAR_CHECK_400 = 0));
	  // Check if previous year is a leap year (29. February/366 days)
	  #tempPrevYearIsLeapyear := (((#tempYearPrevYear MOD #LEAP_YEAR_CHECK_4 = 0) AND NOT (#tempYearPrevYear MOD #LEAP_YEAR_CHECK_100 = 0)) OR (#tempYearPrevYear MOD #LEAP_YEAR_CHECK_400 = 0));

	  // Weekday of 1st January in the current year
	  #tempDate := #date;
	  #tempDate.MONTH := #JANUARY;
	  #tempDate.DAY := #FIRST_DAY;
	  #tempDate := #tempDate + T#0ms;
	  #tempCurrentYearWeekday1Jan := #tempDate.WEEKDAY;

	  // Weekday of 1st January in the next year
	  #tempDate.YEAR := #tempYearNextYear;
	  #tempDate.MONTH := #JANUARY;
	  #tempDate.DAY := #FIRST_DAY;
	  #tempDate := #tempDate + T#0ms;
	  #tempNextYearWeekday1Jan := #tempDate.WEEKDAY;

	  // Weekday of 31st December in the current year
	  #tempDate := #date;
	  #tempDate.MONTH := #DECEMBER;
	  #tempDate.DAY := #LAST_DAY;
	  #tempDate := #tempDate + T#0ms;
	  #tempCurrentYearWeekday31Dec := #tempDate.WEEKDAY;

	  // Weekday of 1st January in the previous year
	  #tempDate.YEAR := #tempYearPrevYear;
	  #tempDate.MONTH := #JANUARY;
	  #tempDate.DAY := #FIRST_DAY;
	  #tempDate := #tempDate + T#0ms;
	  #tempPrevYearWeekday1Jan := #tempDate.WEEKDAY;

	  // Weekday of 31st December in the previous year
	  #tempDate.YEAR := #tempYearPrevYear;
	  #tempDate.MONTH := #DECEMBER;
	  #tempDate.DAY := #LAST_DAY;
	  #tempDate := #tempDate + T#0ms;
	  #tempPrevYearWeekday31Dec := #tempDate.WEEKDAY;

	  // Calculate the week number for USA and other countries
	  // - new week begins whith Sunday
	  // - the week wich contains the 1st January is allways week number 1
	  // - a year has 52 or 53 weeks

	  // Correction of the number of #tempDays depending on the weekday of 1st January
	  CASE #tempCurrentYearWeekday1Jan OF
	    #SUNDAY:
	      #tempDaysCorr := #tempDays - #ONE_DAY;
	    #MONDAY:
	      #tempDaysCorr := #tempDays;
	    #TUESDAY:
	      #tempDaysCorr := #tempDays + #ONE_DAY;
	    #WEDNESDAY:
	      #tempDaysCorr := #tempDays + #TWO_DAYS;
	    #THURSDAY:
	      #tempDaysCorr := #tempDays + #THREE_DAYS;
	    #FRIDAY:
	      #tempDaysCorr := #tempDays + #FOUR_DAYS;
	    #SATURDAY:
	      #tempDaysCorr := #tempDays + #FIVE_DAYS;
	  END_CASE;
	  // calculate the number of the week
	  #tempWeek := #tempDaysCorr / #SEVEN_DAYS + #ONE_WEEK;

	  // Check if current year has 53 weeks
	  #tempIsCurrentYearWith53Weeks := (NOT #tempCurrentYearIsLeapyear AND (#tempCurrentYearWeekday1Jan = #SATURDAY AND #tempCurrentYearWeekday31Dec = #SATURDAY)) OR
	  (#tempCurrentYearIsLeapyear AND ((#tempCurrentYearWeekday1Jan = #SATURDAY AND #tempCurrentYearWeekday31Dec = #SUNDAY) OR (#tempCurrentYearWeekday1Jan = #FRIDAY AND #tempCurrentYearWeekday31Dec = #SATURDAY)));

	  // Correct the week number depending on wheter the current year has 53 weeks
	  IF #tempWeek = #WEEK_53 THEN
	    IF #tempIsCurrentYearWith53Weeks THEN
	      #tempWeek := #WEEK_53;
	    ELSE
	      #tempWeek := #WEEK_1;
	    END_IF;
	  END_IF;

	  IF #tempWeek = #WEEK_54 THEN
	    #tempWeek := #WEEK_1;
	  END_IF;
	END_REGION

	REGION OUTPUTS
	  #calendarDay := #tempDays;
	  #error := FALSE;
	  #status := #STATUS_NO_ERROR;
	  #LGF_GetCalendarWeek_US := #tempWeek;

	  // ENO is not used, forced to TRUE
	  ENO := TRUE;
	END_REGION

END_FUNCTION

FUNCTION "LGF_Random_DInt" : DInt
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : Siemens_Digital_Industries
FAMILY : LGF
NAME : LGF_Random_DInt
   VAR_OUTPUT 
      error : Bool;
      status : Word;
      subfunctionStatus : Word;
   END_VAR

   VAR_TEMP 
      tempTime {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL;
      tempNanoSecondInDWord : DWord;
      tempTimeStatus : Word;
      tempRandomValue : DWord;
   END_VAR

   VAR CONSTANT 
      ZERO : DInt;
      SUB_STATUS_NO_ERROR : Word := 16#0000;
      STATUS_FINISHED_NO_ERROR : Word := 16#0000;
      ERR_RD_SYS_T : Word := 16#8600;
   END_VAR


BEGIN
	REGION Block info header
	  //=============================================================================
	  // SIEMENS AG / (c)Copyright 2017
	  //-----------------------------------------------------------------------------
	  // Title:            LGF_Random_DInt
	  // Comment/Function: Generates a random number in the DInt Range
	  // Library/Family:   LGF(Library General Functions)
	  // Author:           Siemens Digital Industry Support
	  // Tested with:      S7-PLCSIM Advanced V2.0 SP1
	  // Engineering:      TIA Portal V15 Update 2
	  // Restrictions:     ENO disabled - error handling done by error and status
	  // Requirements:     PLC (S7-1200 / S7-1500)
	  //-------------------------------------------------------------------------------
	  // Change log table:
	  // Version  | Date       | Expert in charge       | Changes applied
	  //----------|------------|------------------------|------------------------------
	  // 01.00.00 13.11.2019  Siemens Industry Presales Support
	  //                      First release
	  //                      copied from "LGF_Random_Real"
	  // 03.00.00 23.04.2020  Simatic Systems Support
	  //                      Set version to V3.0.0, harmonize the version of the whole library
	  // 03.00.01 04.02.2021  Simatic Systems Support
	  //                      Insert documentation
	  //=============================================================================
	END_REGION Block info header

	REGION DESCRIPTION
	  (/**/)
	END_REGION DESCRIPTION

	REGION Validation
	  // Read system time
	  #tempTimeStatus := INT_TO_WORD(RD_SYS_T(#tempTime));

	  // Check return variable for error
	  IF (#tempTimeStatus <> #SUB_STATUS_NO_ERROR) THEN
	    #error := true;
	    #status := #ERR_RD_SYS_T;
	    #subfunctionStatus := #tempTimeStatus;
	    #LGF_Random_DInt := #ZERO;
	    RETURN;
	  END_IF;
	END_REGION

	REGION Calculating random number
	  // Converting to double word so individual bytes can be addressed
	  #tempNanoSecondInDWord := UDINT_TO_DWORD(#tempTime.NANOSECOND);

	  // Calculate a random-start-value depending on the time
	  #tempRandomValue.%B3 := #tempNanoSecondInDWord.%B0;
	  #tempRandomValue.%B2 := #tempNanoSecondInDWord.%B1;
	  #tempRandomValue.%B1 := #tempNanoSecondInDWord.%B2;
	  #tempRandomValue.%B0 := #tempNanoSecondInDWord.%B3;

	  #LGF_Random_DInt := DWORD_TO_DINT(#tempRandomValue);

	  #error := false;
	  #status := #STATUS_FINISHED_NO_ERROR;
	  #subfunctionStatus := #SUB_STATUS_NO_ERROR;
	  // ENO mechanism is not used
	  ENO := TRUE;
	END_REGION

END_FUNCTION

FUNCTION "LGF_CalcCRC32Advanced" : DWord
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : Siemens_Industry_Support
FAMILY : LGF
NAME : LGF_CRC32Advanced
   VAR_INPUT 
      initValue { S7_PredefinedAssignment := '16#00'} : DWord;
      mask { S7_PredefinedAssignment := '16#00'} : DWord;
      finalXorValue { S7_PredefinedAssignment := '16#00'} : DWord;
