   END_VAR


BEGIN
	REGION Block info header
	  //=============================================================================
	  // SIEMENS AG 
	  //-----------------------------------------------------------------------------
	  // Title:            LGF_SystemClockGen
	  // Comment/Function: This function generates the same clocksignals as the system clock memory.
	  // Library/Family:   LGF (from SICAR)
	  // Author:           Simatic Systems Support (SICAR)
	  // Tested with:      S7-PLCSIM Advanced V2.0 SP1 and PLC S7 1516F-3 PN/DP
	  // Engineering:      TIA Portal V17
	  // Restrictions:     ENO disabled
	  // Requirements:     PLC (S7-1200 / S7-1500)
	  //-------------------------------------------------------------------------------
	  // Change log table:
	  // Version  | Date       | Expert in charge        | Changes applied
	  //----------|------------|-------------------------|------------------------------
	  // 01.00.00 | 01.10.2023 | SICAR                   | First released version
	  // 01.00.01 | 06.05.2025 | Simatic Systems Support | Integration into LGF
	  //=============================================================================
	END_REGION Block info header

	REGION DESCRIPTION
	  (/**/)
	END_REGION DESCRIPTION

	REGION Read current cyletime
	  #tempCycleTimeMicroSecond := LREAL_TO_UDINT((RUNTIME(#statMemRuntime)) * #SECOND_TO_MICROSECOND);
	END_REGION

	REGION Calculation of settings and program execution for 10Hz
	  // Sum up the cycletime
	  #statCalcTime.clk10Hz += #tempCycleTimeMicroSecond;

	  // Calculation if positive flag is active
	  IF NOT #statStateFlag.clk10Hz AND #statCalcTime.clk10Hz / #PULSE_WIDTH_CLK10HZ >= 1 THEN
	    //Set statusflag and write remainder with modulo function
	    #statStateFlag.clk10Hz := TRUE;
	    #statCalcTime.clk10Hz := #statCalcTime.clk10Hz MOD #PULSE_WIDTH_CLK10HZ;

	    // Calculation if negative flag is active  
	  ELSIF #statStateFlag.clk10Hz AND #statCalcTime.clk10Hz / #PULSE_WIDTH_CLK10HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function 
	    #statStateFlag.clk10Hz := FALSE;
	    #statCalcTime.clk10Hz := #statCalcTime.clk10Hz MOD #PULSE_WIDTH_CLK10HZ;
	  END_IF;
	END_REGION

	REGION Calculation of settings and program execution for 5Hz
	  // Sum up the cycletime
	  #statCalcTime.clk5Hz += #tempCycleTimeMicroSecond;

	  // Calculation if positive flag is active
	  IF NOT #statStateFlag.clk5Hz AND #statCalcTime.clk5Hz / #PULSE_WIDTH_CLK5HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function
	    #statStateFlag.clk5Hz := TRUE;
	    #statCalcTime.clk5Hz := #statCalcTime.clk5Hz MOD #PULSE_WIDTH_CLK5HZ;

	    // Calculation if negative flag is active  
	  ELSIF #statStateFlag.clk5Hz AND #statCalcTime.clk5Hz / #PULSE_WIDTH_CLK5HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function 
	    #statStateFlag.clk5Hz := FALSE;
	    #statCalcTime.clk5Hz := #statCalcTime.clk5Hz MOD #PULSE_WIDTH_CLK5HZ;
	  END_IF;
	END_REGION

	REGION Calculation of settings and program execution for 2_5Hz
	  // Sum up the cycletime
	  #statCalcTime.clk2Hz5 += #tempCycleTimeMicroSecond;

	  // Calculation if positive flag is active
	  IF NOT #statStateFlag.clk2Hz5 AND #statCalcTime.clk2Hz5 / #PULSE_WIDTH_CLK2_5HZ >= 1 THEN
	    //Set statusflag and write remainder with modulo function
	    #statStateFlag.clk2Hz5 := TRUE;
	    #statCalcTime.clk2Hz5 := #statCalcTime.clk2Hz5 MOD #PULSE_WIDTH_CLK2_5HZ;

	    // Calculation if negative flag is active  
	  ELSIF #statStateFlag.clk2Hz5 AND #statCalcTime.clk2Hz5 / #PULSE_WIDTH_CLK2_5HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function 
	    #statStateFlag.clk2Hz5 := FALSE;
	    #statCalcTime.clk2Hz5 := #statCalcTime.clk2Hz5 MOD #PULSE_WIDTH_CLK2_5HZ;
	  END_IF;
	END_REGION

	REGION Calculation of settings and program execution for 2Hz
	  // Sum up the cycletime
	  #statCalcTime.clk2Hz += #tempCycleTimeMicroSecond;

	  // Calculation if positive flag is active
	  IF NOT #statStateFlag.clk2Hz AND #statCalcTime.clk2Hz / #PULSE_WIDTH_CLK2HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function
	    #statStateFlag.clk2Hz := TRUE;
	    #statCalcTime.clk2Hz := #statCalcTime.clk2Hz MOD #PULSE_WIDTH_CLK2HZ;

	    // Calculation if negative flag is active  
	  ELSIF #statStateFlag.clk2Hz AND #statCalcTime.clk2Hz / #PULSE_WIDTH_CLK2HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function 
	    #statStateFlag.clk2Hz := FALSE;
	    #statCalcTime.clk2Hz := #statCalcTime.clk2Hz MOD #PULSE_WIDTH_CLK2HZ;
	  END_IF;
	END_REGION

	REGION Calculation of settings and program execution for 1_25Hz
	  // Sum up the cycletime
	  #statCalcTime.clk1Hz25 += #tempCycleTimeMicroSecond;

	  // Calculation if positive flag is active
	  IF NOT #statStateFlag.clk1Hz25 AND #statCalcTime.clk1Hz25 / #PULSE_WIDTH_CLK1_25HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function
	    #statStateFlag.clk1Hz25 := TRUE;
	    #statCalcTime.clk1Hz25 := #statCalcTime.clk1Hz25 MOD #PULSE_WIDTH_CLK1_25HZ;

	    // Calculation if negative flag is active  
	  ELSIF #statStateFlag.clk1Hz25 AND #statCalcTime.clk1Hz25 / #PULSE_WIDTH_CLK1_25HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function 
	    #statStateFlag.clk1Hz25 := FALSE;
	    #statCalcTime.clk1Hz25 := #statCalcTime.clk1Hz25 MOD #PULSE_WIDTH_CLK1_25HZ;
	  END_IF;
	END_REGION

	REGION Calculation of settings and program execution for 1Hz
	  //Sum up the cycletime
	  #statCalcTime.clk1Hz += #tempCycleTimeMicroSecond;

	  // Calculation if positive flag is active
	  IF NOT #statStateFlag.clk1Hz AND #statCalcTime.clk1Hz / #PULSE_WIDTH_CLK1HZ >= 1 THEN
	    //Set statusflag and write remainder with modulo function
	    #statStateFlag.clk1Hz := TRUE;
	    #statCalcTime.clk1Hz := #statCalcTime.clk1Hz MOD #PULSE_WIDTH_CLK1HZ;

	    // Calculation if negative flag is active  
	  ELSIF #statStateFlag.clk1Hz AND #statCalcTime.clk1Hz / #PULSE_WIDTH_CLK1HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function 
	    #statStateFlag.clk1Hz := FALSE;
	    #statCalcTime.clk1Hz := #statCalcTime.clk1Hz MOD #PULSE_WIDTH_CLK1HZ;
	  END_IF;
	END_REGION

	REGION Calculation of settings and program execution for 0_625Hz
	  // Sum up the cycletime
	  #statCalcTime.clk0Hz625 += #tempCycleTimeMicroSecond;

	  // Calculation if positive flag is active
	  IF NOT #statStateFlag.clk0Hz625 AND #statCalcTime.clk0Hz625 / #PULSE_WIDTH_CLK0_625HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function
	    #statStateFlag.clk0Hz625 := TRUE;
	    #statCalcTime.clk0Hz625 := #statCalcTime.clk0Hz625 MOD #PULSE_WIDTH_CLK0_625HZ;

	    // Calculation if negative flag is active  
	  ELSIF #statStateFlag.clk0Hz625 AND #statCalcTime.clk0Hz625 / #PULSE_WIDTH_CLK0_625HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function 
	    #statStateFlag.clk0Hz625 := FALSE;
	    #statCalcTime.clk0Hz625 := #statCalcTime.clk0Hz625 MOD #PULSE_WIDTH_CLK0_625HZ;
	  END_IF;
	END_REGION

	REGION Calculation of settings and program execution for 0_5Hz
	  // Sum up the cycletime
	  #statCalcTime.clk0Hz5 += + #tempCycleTimeMicroSecond;

	  // Calculation if positive flag is active
	  IF NOT #statStateFlag.clk0Hz5 AND #statCalcTime.clk0Hz5 / #PULSE_WIDTH_CLK0_5HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function
	    #statStateFlag.clk0Hz5 := TRUE;
	    #statCalcTime.clk0Hz5 := #statCalcTime.clk0Hz5 MOD #PULSE_WIDTH_CLK0_5HZ;

	    // Calculation if negative flag is active  
	  ELSIF #statStateFlag.clk0Hz5 AND #statCalcTime.clk0Hz5 / #PULSE_WIDTH_CLK0_5HZ >= 1 THEN
	    // Set statusflag and write remainder with modulo function 
	    #statStateFlag.clk0Hz5 := FALSE;
	    #statCalcTime.clk0Hz5 := #statCalcTime.clk0Hz5 MOD #PULSE_WIDTH_CLK0_5HZ;
	  END_IF;
	END_REGION

	REGION Writing Output
	  // Write statusbit to output
	  #clk10Hz := #statClockByte.%X0 := NOT #statStateFlag.clk10Hz;
	  #clk5Hz := #statClockByte.%X1 := NOT #statStateFlag.clk5Hz;
	  #clk2Hz5 := #statClockByte.%X2 := NOT #statStateFlag.clk2Hz5;
	  #clk2Hz := #statClockByte.%X3 := NOT #statStateFlag.clk2Hz;
	  #clk1Hz25 := #statClockByte.%X4 := NOT #statStateFlag.clk1Hz25;
	  #clk1Hz := #statClockByte.%X5 := NOT #statStateFlag.clk1Hz;
	  #clk0Hz625 := #statClockByte.%X6 := NOT #statStateFlag.clk0Hz625;
	  #clk0Hz5 := #statClockByte.%X7 := NOT #statStateFlag.clk0Hz5;

	  REGION Generating Edges
	    IF #statClockByte = #statClockBytePrevious THEN
	      #statClockByteRisingEdge :=
	      #statClockByteFallingEdge := 16#00;
	    ELSE
	      #statClockByteRisingEdge := (#statClockByte XOR #statClockBytePrevious) AND #statClockByte;
	      #statClockByteFallingEdge := (#statClockByte XOR #statClockBytePrevious) AND NOT #statClockByte;
	    END_IF;
	    #statClockBytePrevious := #statClockByte;
	  END_REGION

	  #clockByte := #statClockByte;
	  #clockByteRisingEdge := #statClockByteRisingEdge;
	  #clockByteFallingEdge := #statClockByteFallingEdge;

	  SCATTER(IN  := #statClockByte,
	          OUT => #clockDataType);
	  SCATTER(IN  := #statClockByteRisingEdge,
	          OUT => #clockDataTypeRisingEdge);
	  SCATTER(IN  := #statClockByteFallingEdge,
	          OUT => #clockDataTypeFallingEdge);

	  // ENO is not needed here, forced to TRUE
	  ENO := TRUE;
	END_REGION

END_FUNCTION_BLOCK

FUNCTION "LGF_DifferenceQuotientFC" : Void
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : Siemens_Digital_Industry
FAMILY : LGF
NAME : LGF_DifferenceQuotientFC
   VAR_INPUT 
      deltaT : LReal;
   END_VAR

   VAR_OUTPUT 
      error : Bool;
      status : Word;
   END_VAR

   VAR_IN_OUT 
      values : Array[*] of LReal;
      derivatedValues : Array[*] of LReal;
   END_VAR

   VAR_TEMP 
      tempLowerBound : Int;
      tempUpperBound : Int;
      tempNumberOfElements : Int;
      tempLowerBoundOut : Int;
      tempUpperBoundOut : Int;
      tempNumberOfElementsOut : Int;
      tempCount : Int;
      tempDivision : LReal;
   END_VAR

   VAR CONSTANT 
      ZERO : LReal := 0.0;
      NUMBER_OF_ELEMENTS_CORRECTION : Int := 1;
      DIMENSION_ONE : UDInt := 1;
      MINIMUM_ARRAY_ELEMENTS : UDInt := 4;
      STATUS_FINISHED_NO_ERROR : Word := 16#0000;
      ERR_DELTA_T : Word := 16#8200;
      ERR_ARRAYS_DIFFERENT : Word := 16#8400;
      ERR_NOT_ENOUGH_VALUES : Word := 16#8401;
   END_VAR


BEGIN
	REGION Block info header
	  //============================================================================
	  // SIEMENS AG / (c)Copyright 2017
	  //----------------------------------------------------------------------------
	  // Title:            LGF_DifferenceQuotientFC
	  // Comment/Function: This function differentiates a signal
	  // Library/Family:   LGF(Library General Functions)
	  // Author:           Siemens Digital Industry Support
	  // Tested with:      S7-PLCSIM Advanced V2.0 SP1
	  // Engineering:      TIA Portal V15 Update 2
	  // Restrictions:     ENO disabled - error handling done by error and status
	  // Requirements:     PLC (S7-1200 / S7-1500)
	  //----------|------------|------------------------|----------------------------
	  // Version  | Date       | Expert in charge       | Changes applied
	  //----------|------------|------------------------|----------------------------
	  // 01.00.00 | 23.11.2018 | Siemens Industry Online Support
	  //                       | First released version
	  // 01.00.01 | 15.11.2019 | Simatic Systems Support
	  //                       | Regions, comments and constants are added
	  // 03.00.00 | 23.04.2020 | Simatic Systems Support
	  //                       | Set version to V3.0.0, harmonize the version of the whole library
	  // 03.00.01 | 06.04.2021 | Simatic Systems Support
	  //                       | Insert documentation
	  //=============================================================================
	END_REGION Block info header

	REGION DESCRIPTION
	  (/**/)
	END_REGION DESCRIPTION

	REGION Checking number of lower and upper bound  
	  #tempLowerBound := DINT_TO_INT(LOWER_BOUND(ARR := #values, DIM := #DIMENSION_ONE));
	  #tempUpperBound := DINT_TO_INT(UPPER_BOUND(ARR := #values, DIM := #DIMENSION_ONE));

	  #tempNumberOfElements := #tempUpperBound - #tempLowerBound + #NUMBER_OF_ELEMENTS_CORRECTION;

	  #tempLowerBoundOut := DINT_TO_INT(LOWER_BOUND(ARR := #derivatedValues, DIM := #DIMENSION_ONE));
	  #tempUpperBoundOut := DINT_TO_INT(UPPER_BOUND(ARR := #derivatedValues, DIM := #DIMENSION_ONE));

	  #tempNumberOfElementsOut := #tempUpperBoundOut - #tempLowerBoundOut + #NUMBER_OF_ELEMENTS_CORRECTION;

	  //check if size of arrays is ok
	  IF #tempNumberOfElementsOut <> #tempNumberOfElements THEN
	    #error := true;
	    #status := #ERR_ARRAYS_DIFFERENT;
	    RETURN;
	    // check size of arrays if the are big enough
	  ELSIF #tempNumberOfElements <= #MINIMUM_ARRAY_ELEMENTS THEN
	    #error := true;
	    #status := #ERR_NOT_ENOUGH_VALUES;
	    RETURN;
	    // DeltaT cannot be zero, devision by zero not possible.
	  ELSIF #deltaT = #ZERO THEN
	    #error := true;
	    #status := #ERR_DELTA_T;
	    RETURN;
	  END_IF;
	END_REGION

	REGION Calculating the derrivate values  
	  // calculate derivate value 
	  // keep the "Magic Numbers" as they are part of the Math equation and would extent the formular in a unreadable way
	  // yÌ‡(n)=1/12h*(y(n-2)-8y(n-1)+8y(n+1)-y(n+2))
	  // h = deltaT
	  #tempDivision := 1.0 / (12.0 * #deltaT);
	  FOR #tempCount := #tempLowerBound + 2 TO #tempUpperBound - 2 DO
	    #derivatedValues[#tempCount] := #tempDivision * (#values[#tempCount - 2] - 8 * #values[#tempCount - 1] + 8 * #values[#tempCount + 1] - #values[#tempCount + 2]);
	  END_FOR;

	  // yÌ‡(n-2)=1/84h*(-125y(n-2)+136y(n-1)+48y(n)-88y(n+1)+29y(n+2))
	  // h = deltaT
	  #tempDivision := 1.0 / (84.0 * #deltaT);
	  #derivatedValues[0] := #tempDivision * (-125 * #values[0] + 136 * #values[1] + 48 * #values[2] - 88 * #values[3] + 29 * #values[4]);

	  // yÌ‡(n-1)=1/84h*(-38y(n-2)-2y(n-1)+24y(n)+26y(n+1)-10y(n+2))
	  // h = deltaT
	  #derivatedValues[1] := #tempDivision * (-38 * #values[0] - 2 * #values[1] + 24 * #values[2] + 26 * #values[3] - 10 * #values[4]);

	  // yÌ‡(n+1)=1/84h(10y(n-2)-26y(n-1)-24y(n)+2y(n+1)+38y(n+2))
	  // h = deltaT
	  #derivatedValues[#tempUpperBound - 1] := #tempDivision * (10 * #values[#tempUpperBound - 4] - 26 * #values[#tempUpperBound - 3] - 24 * #values[#tempUpperBound - 2] + 2 * #values[#tempUpperBound - 1] + 38 * #values[#tempUpperBound]);

	  // y(n-2)=1/84h(-29y(n-2)+88y(n-1)-48y(n)-136y(n+1)+125y(n+2))
	  // h = deltaT
	  #derivatedValues[#tempUpperBound] := #tempDivision * (-29 * #values[#tempUpperBound - 4] + 88 * #values[#tempUpperBound - 3] - 48 * #values[#tempUpperBound - 2] - 136 * #values[#tempUpperBound - 1] + 125 * #values[#tempUpperBound]);

	  #error := false;
	  #status := #STATUS_FINISHED_NO_ERROR;
	  // ENO mechanism is not used - forced to true.
	  ENO := TRUE;
	END_REGION

END_FUNCTION

FUNCTION_BLOCK "LGF_FileReadWriteDelete"
{ S7_Optimized_Access := 'TRUE' }
AUTHOR : 'Simatic Systems Support'
FAMILY : LGF
NAME : LGF_FileReadWriteDelete
   VAR_INPUT 
      executeRead : Bool;
      executeWrite : Bool;
      executeDelete : Bool;
      fileName : String;
      parameter { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : "LGF_typeFileReadWriteDeleteParameter";
   END_VAR

   VAR_OUTPUT 
      done { ExternalWritable := 'False'} : Bool;
      busy { ExternalWritable := 'False'} : Bool;
      error { ExternalWritable := 'False'} : Bool;
      status { ExternalWritable := 'False'} : Word := #STATUS_NO_CALL;
      dataLength { ExternalWritable := 'False'} : DInt;
      fileDoesNotExist { ExternalWritable := 'False'} : Bool;
      timestamps { ExternalWritable := 'False'} : "LGF_typeFileReadWriteDeleteTimestamps";
      diagnostics { ExternalWritable := 'False'} : "LGF_typeDiagnostics";
   END_VAR

   VAR_IN_OUT 
      bufferByteArray : Array[*] of Byte;
      data : Variant;
   END_VAR

   VAR 
      statInternal { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Struct
         executePrevious { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         done { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         busy { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         error { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
         diagnostics { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : "LGF_typeDiagnostics" := (#STATUS_NO_CALL, (), ());
         emptyDiagnostics { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : "LGF_typeDiagnostics" := (#STATUS_NO_CALL, (), ());
      END_STRUCT;
      instFileRead {InstructionName := 'FileReadC'; LibVersion := '1.0'; ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : FileReadC;
      instFileWrite {InstructionName := 'FileWriteC'; LibVersion := '1.0'; ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : FileWriteC;
      instFileDelete {InstructionName := 'FileDelete'; LibVersion := '1.0'; ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : FileDelete;
      statMainState { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : DInt := #STATE_NO_PROCESSING;
      statParameterDataIsByteArray { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
      statAsynchronusMode { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : Bool;
      statBufferSize { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : DInt;
      statLengthData { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : DInt;
      statLengthFile { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : DInt := 0;
      statLengthTimeStamp { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'} : DInt := 0;
      statTimestamps { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : "LGF_typeFileReadWriteDeleteTimestamps";
      statTimestampDefault { ExternalAccessible := 'False'; ExternalVisible := 'False'; ExternalWritable := 'False'; S7_SetPoint := 'False'} : "LGF_typeFileReadWriteDeleteTimestamp" := (#DEFAULT_DATE, FALSE);
   END_VAR

   VAR_TEMP 
      tempExecute : Bool;
      tempExitStateLoop : Bool;
      tempResult : Word;
      tempBufferPosition : DInt;
      tempPathPrefix : String;
   END_VAR

   VAR CONSTANT 
      DEFAULT_DATE {InstructionName := 'DTL'; LibVersion := '1.0'} : DTL := DTL#1970-01-01-00:00:00;
      PATH_PREFIX : String := 'UserFiles/';
      EMPTY_BYTE : Byte := 16#00;
      FILE_DELETE_PATH_NAME_NOT_EXIST : Word := 16#8091;
      FILE_DELETE_FILE_NOT_EXIST : Word := 16#8092;
      STATE_NO_PROCESSING : DInt := 0;
      STATE_FILE_READ_INIT : DInt := 1;
      STATE_FILE_WRITE_INIT : DInt := 2;
      STATE_FILE_DELETE_INIT : DInt := 3;
      STATE_FILE_READ : DInt := 10;
      STATE_FILE_READ_PARAMETER_TO_INTERFACE : DInt := 11;
      STATE_FILE_WRITE_PARAMETER : DInt := 20;
      STATE_FILE_WRITE : DInt := 21;
      STATE_FILE_DELETE : DInt := 30;
      STATUS_EXECUTION_FINISHED : Word := 16#0000;
      STATUS_NO_CALL : Word := 16#7000;
      STATUS_FIRST_CALL : Word := 16#7001;
      STATUS_SUBSEQUENT_CALL : Word := 16#7002;
      ERR_UNDEFINED_STATE : Word := 16#8600;
      "- - - Application Error codes" : Word;
      ERR_BUFFER_LOWERBOUND : Word := 16#8201;
      ERR_BUFFER_ARRAY_TO_SMALL_TO_COPY : Word := 16#8202;
      ERR_MULTIPLE_COMMANDS : Word := 16#8400;
      ERR_FILE_PATH : Word := 16#8401;
      ERR_READ_TIME_LOCAL : Word := 16#8402;
      ERR_READ_TIME_SYSTEM : Word := 16#8403;
      ERR_FILE_SIZE_GRATER_THEN_DATA_SIZE : Word := 16#8411;
      ERR_FILE_SIZE_LESS_THEN_DATA_SIZE : Word := 16#8412;
      ERR_MOVE_BLK_VARIANT : Word := 16#8601;
      ERR_SERIALIZE_DATA : Word := 16#8602;
      ERR_DESERIALIZE_DATA : Word := 16#8603;
      ERR_SERIALIZE_DATE_TIMESTAMP : Word := 16#8604;
      ERR_DESERIALIZE_DATE_TIMESTAMP : Word := 16#8605;
      ERR_FILE_READ_INIT : Word := 16#8610;
      ERR_FILE_READ : Word := 16#8611;
      ERR_FILE_WRITE_INIT : Word := 16#8620;
      ERR_FILE_WRITE : Word := 16#8621;
      ERR_FILE_DELETE_INIT : Word := 16#8630;
      ERR_FILE_DELETE : Word := 16#8631;
   END_VAR


BEGIN
	REGION BLOCK INFO HEADER
	  //================================================================================
	  //SIEMENS AG
	  //(c)Copyright 2017 All Rights Reserved
	  //--------------------------------------------------------------------------------
	  //Library:       LGF (Library General Functions) 
	  //Tested with:   S7-1510SP-F
	  //Engineering:   TIA Portal V15
	  //Restrictions:  ---
	  //Requirements:  S7-1500/S7-1500T FW 2.5
	  //Functionality: The function `LGF_FileReadWriteDelete` can be used to read, write or delete a file to/from the SIMATIC Memory Card (SMC).
	  //To read or write the data, it is necessary to deserialize/serialize it, which the function already does for the user.
	  //For this purpose, an external buffer in the form of a Byte array must be connected, which can hold the amount of data;
	  //if the buffer is too small, an error is output.
	  //The file name must always be specified in full together with the folder name AND the file extension in the following format: `UserFiles/Test.dat`.
	  //--------------------------------------------------------------------------------
	  //Change log table:
	  //Version  | Date       | Expert in charge        | Changes applied
	  //---------|------------|-------------------------|------------------------------
	  //01.00.00 | 10.07.2025 | Simatic Systems Support | First released version
	  //================================================================================
	END_REGION

	REGION DESCRIPTION
	  (/**/)

	  (/**/)

	  (/**/)

	  (/**/)
	END_REGION DESCRIPTION

	REGION TRIGGERING
	  #tempExecute := #executeRead OR #executeWrite OR #executeDelete; // Work with temporary value / create process image
	  IF (#tempExecute = TRUE) AND (#statInternal.executePrevious = FALSE) // Check if FB is triggered
	    // FB shall finish current job before new job can be started with rising edge of execute
	    AND (#statInternal.diagnostics.status = #STATUS_NO_CALL)
	  THEN // First call; initialize FB
	    #statInternal.done := FALSE;
	    #statInternal.busy := TRUE;
	    #statInternal.error := FALSE;

	    // Initialize functionality: reset of variables, diagnostics, etc.
	    #statInternal.diagnostics := #statInternal.emptyDiagnostics;
	    #statInternal.diagnostics.status := #STATUS_FIRST_CALL;
	    #diagnostics := #statInternal.emptyDiagnostics;

	    // Initialize functionality: call subsidiary FBs with FALSE
	    #instFileRead(REQ := FALSE);
	    #instFileWrite(REQ := FALSE);
	    #instFileDelete(REQ := FALSE);

	    REGION Generate Timestamp of command
	      IF #parameter.timeStampIsLocal THEN
	        #tempResult := INT_TO_WORD(RD_LOC_T(#statTimestamps.lastCommand.dateAndTime));
	      ELSE
	        #tempResult := INT_TO_WORD(RD_SYS_T(#statTimestamps.lastCommand.dateAndTime));
	      END_IF;
	      #statTimestamps.lastCommand.timestampIsLocal := #parameter.timeStampIsLocal;
	    END_REGION

	    IF FALSE // Multiple Commands set?
	      OR (#executeRead AND #executeWrite)
	      OR (#executeRead AND #executeDelete)
	      OR (#executeWrite AND #executeDelete)
	    THEN
	      #statInternal.diagnostics.status := #ERR_MULTIPLE_COMMANDS;
	      #statInternal.diagnostics.stateNumber := #STATE_NO_PROCESSING;
	      #statMainState := #STATE_NO_PROCESSING;

	    ELSIF #tempResult.%X15 THEN // Read time stamp throws an error?
	      IF #parameter.timeStampIsLocal THEN
	        #statInternal.diagnostics.status := #ERR_READ_TIME_LOCAL;
	      ELSE
	        #statInternal.diagnostics.status := #ERR_READ_TIME_SYSTEM;
	      END_IF;

	      #statInternal.diagnostics.stateNumber := #STATE_NO_PROCESSING;
	      #statMainState := #STATE_NO_PROCESSING;

	      // State machine - start processing
	    ELSIF #executeRead THEN
	      #statMainState := #STATE_FILE_READ_INIT;
	    ELSIF #executeWrite THEN
	      #statMainState := #STATE_FILE_WRITE_INIT;
	    ELSIF #executeDelete THEN
	      #statMainState := #STATE_FILE_DELETE_INIT;
	    END_IF;

	  ELSIF (#statInternal.diagnostics.status = #STATUS_FIRST_CALL) THEN
	    #statInternal.diagnostics.status := #STATUS_SUBSEQUENT_CALL;
	  END_IF;

	  // Edge detection 'execute' input
	  #statInternal.executePrevious := #tempExecute;
	END_REGION TRIGGERING

	IF (#statInternal.diagnostics.status = #STATUS_NO_CALL) THEN // Nothing to do -> End here to reduce "system load"
	  RETURN;
	END_IF;

	REGION STATE_MACHINE
	  // If one ore more runs in the same cycle are necessary, set #tempStateChangeActive := TRUE;
	  REPEAT
	    // Reset #tempExitStateLoop := TRUE; as its defined state to prevent endless loop
	    #tempExitStateLoop := TRUE;

	    // State machine of FB
	    CASE #statMainState OF
	      #STATE_NO_PROCESSING: // No processing active (Note: this state must always be present and left empty)
	        ;

	      #STATE_FILE_READ_INIT,
	      #STATE_FILE_WRITE_INIT,
	      #STATE_FILE_DELETE_INIT:
	        REGION Init
	          REGION Verify File Path
	            #tempPathPrefix := #PATH_PREFIX;

	            FOR #tempBufferPosition := 1 TO DINT_TO_INT(LEN(#tempPathPrefix)) DO
	              IF #fileName[#tempBufferPosition] <> #tempPathPrefix[#tempBufferPosition] THEN
	                // Characters arenÃ„t identiacal
	                #statInternal.diagnostics.status := #ERR_FILE_PATH;
	                #statInternal.diagnostics.subfunctionStatus := DINT_TO_WORD(#tempBufferPosition);
	                EXIT; // Stop execution and leave block
	              END_IF;
	            END_FOR;

	            // Leaving the outer loop as well in case of error
	            IF #statInternal.diagnostics.status.%X15 THEN
	              EXIT; // Stop execution and leave block
	            END_IF;
	          END_REGION

	          REGION Proof if delete is called to jump to the next step
	            // If delet is called - move on to next state
	            // jump over the others by jump over loop iterator with continue
	            IF #statMainState = #STATE_FILE_DELETE_INIT THEN
	              REGION Delete File handler init
	                // Init file read with false
	                #instFileDelete(REQ := FALSE);

	                IF #instFileDelete.Error THEN
	                  #statInternal.diagnostics.status := #ERR_FILE_DELETE_INIT;
	                  #statInternal.diagnostics.subfunctionStatus := #instFileDelete.Status;
	                  EXIT; // Stop execution and leave block
	                END_IF;

	                // Next state
	                #statMainState := #STATE_FILE_DELETE;
	                // ASYNCRONUS - set to true:
	                #tempExitStateLoop := #statAsynchronusMode;
	                CONTINUE;
	              END_REGION
	            END_IF;
	          END_REGION

	          REGION Verify buffer boundary
	            // Check buffer size
	            // Check lower boundary
	            #tempResult := DINT_TO_WORD(LOWER_BOUND(ARR := #bufferByteArray, DIM := 1));

	            IF 0 <> #tempResult THEN // ERROR: buffer lowerbound has to be 0
	              #statInternal.diagnostics.status := #ERR_BUFFER_LOWERBOUND;
	              #statInternal.diagnostics.subfunctionStatus := #tempResult;
	              EXIT; // Stop execution and leave block
	            END_IF;

	            // Get upper boundary and calc size of buffer
	            #statBufferSize := UPPER_BOUND(ARR := #bufferByteArray, DIM := 1) + 1;
	          END_REGION

	          // Verify parameter data types
	          REGION Set TimeStamp length
	            IF #parameter.includeTimestamp THEN
	              #statLengthTimeStamp := 14;
	            ELSE
	              #statLengthTimeStamp := 0;
	            END_IF;
	          END_REGION

	          REGION Verify parameter data types
	            // Check type of Parameter attached to IN/OUT
	            // If Array proceed here, Array limits checked by move block variant
	            IF IS_ARRAY(#data) AND TypeOfElements(#data) = Byte THEN
	              #statParameterDataIsByteArray := TRUE;
	              // Get array size
	              #statLengthData := UDINT_TO_DINT(CountOfElements(#data));

	              // Verify if buffer is big enough to store data
	              IF #statBufferSize < (#statLengthData + #statLengthTimeStamp) THEN
	                // ERROR: buffer array is to small for data array
	                #statInternal.diagnostics.status := #ERR_BUFFER_ARRAY_TO_SMALL_TO_COPY;
	                #statInternal.diagnostics.subfunctionStatus := 16#0000;
	                EXIT; // Stop execution and leave block
	              END_IF;
	            END_IF;
	          END_REGION

	          REGION Proof if read is called to jump to the next step
	            IF #statMainState = #STATE_FILE_READ_INIT THEN
	              REGION Check array to see if the length is sufficient to store the read data
	                #statParameterDataIsByteArray := FALSE;
	                #statLengthData := 0;

	                #tempResult := INT_TO_WORD(
	                                           Serialize(SRC_VARIABLE := #data,
	                                                     DEST_ARRAY => #bufferByteArray,
	                                                     POS := #statLengthData));

	                // Check if serialize parameter structure was successful, otherwise set ERROR
	                IF #STATUS_EXECUTION_FINISHED <> #tempResult THEN
	                  #statInternal.diagnostics.status := #ERR_SERIALIZE_DATA;
	                  #statInternal.diagnostics.subfunctionStatus := #tempResult;
	                  EXIT; // Stop execution and leave block
	                END_IF;
	              END_REGION

	              REGION Read File handler init
	                // Set length for FileRead call
	                IF #parameter.dataLengthMustMatch THEN
	                  #instFileRead.Length := 0;
	                ELSE
	                  #instFileRead.Length := DINT_TO_UDINT(#statLengthData + #statLengthTimeStamp);
	                END_IF;

	                // Init file read with false
	                #instFileRead(REQ := FALSE);

	                IF #instFileRead.Error THEN
	                  #statInternal.diagnostics.status := #ERR_FILE_READ_INIT;
	                  #statInternal.diagnostics.subfunctionStatus := #instFileRead.Status;
	                  EXIT; // Stop execution and leave block
	                END_IF;

	                // Next state
	                #statMainState := #STATE_FILE_READ;
	                // ASYNCRONUS - set to true:
	                #tempExitStateLoop := #statAsynchronusMode;
	                CONTINUE;
	              END_REGION
	            END_IF;
	          END_REGION

	          REGION Proof if write is called to jump to the next step
	            IF #statMainState = #STATE_FILE_WRITE_INIT THEN
	              REGION File handler init
	                // Init file write with false
	                #instFileWrite(REQ := FALSE);

	                IF #instFileWrite.Error THEN
	                  #statInternal.diagnostics.status := #ERR_FILE_WRITE_INIT;
	                  #statInternal.diagnostics.subfunctionStatus := #instFileWrite.Status;
	                  EXIT; // Stop execution and leave block
	                END_IF;

	                // Next state
	                #statMainState := #STATE_FILE_WRITE_PARAMETER;
	                // ASYNCRONUS - set to true:
	                #tempExitStateLoop := #statAsynchronusMode;
	                CONTINUE;
	              END_REGION
	            END_IF;
	          END_REGION
	        END_REGION

	      #STATE_FILE_READ:
	        REGION File read - File handling
	          // Set file handler request to true
	          #instFileRead(REQ    := TRUE,
	                        Name   := #fileName,
	                        Offset := UDINT#0,
	                        Data   := #bufferByteArray);

	          IF #instFileRead.Error THEN
	            #statLengthFile := UDINT_TO_DINT(#instFileRead.ResultLength);
	            #statInternal.diagnostics.status := #ERR_FILE_READ;
	            #statInternal.diagnostics.subfunctionStatus := #instFileRead.Status;
	            EXIT; // Stop execution and leave block

	          ELSIF #instFileRead.Done THEN
	            #statLengthFile := UDINT_TO_DINT(#instFileRead.ResultLength);
	            // Check file length parameter
	            IF TRUE
	              AND #parameter.dataLengthMustMatch
	              AND (#statLengthFile > (#statLengthData + #statLengthTimeStamp))
	            THEN
	              #statTimestamps.file := #statTimestampDefault;
	              #statInternal.diagnostics.status := #ERR_FILE_SIZE_GRATER_THEN_DATA_SIZE;
	              #statInternal.diagnostics.subfunctionStatus := DINT_TO_WORD((#statLengthData + #statLengthTimeStamp));
	              EXIT; // Stop execution and leave block

	            ELSIF TRUE
	              AND #parameter.dataLengthMustMatch
	              AND (#statLengthFile < (#statLengthData + #statLengthTimeStamp))
	            THEN
	              #statTimestamps.file := #statTimestampDefault;
	              #statInternal.diagnostics.status := #ERR_FILE_SIZE_LESS_THEN_DATA_SIZE;
	              #statInternal.diagnostics.subfunctionStatus := DINT_TO_WORD((#statLengthData + #statLengthTimeStamp));
	              EXIT; // Stop execution and leave block

	            ELSE
	              // Next state
	              #statMainState := #STATE_FILE_READ_PARAMETER_TO_INTERFACE;
	              // ASYNCRONUS - set to true:
	              #tempExitStateLoop := #statAsynchronusMode;
	            END_IF;
	          END_IF;
	        END_REGION

	      #STATE_FILE_READ_PARAMETER_TO_INTERFACE:
	        REGION File read - Parameter handling
	          // Init buffer size/position pointer
	          #tempBufferPosition := 0;

	          REGION Include time stamp
	            IF #parameter.includeTimestamp THEN
	              // Deserialize Time Stamp into buffer
	              // Length of timestamp is 14 byte
	              #tempResult := INT_TO_WORD(
	                                         Deserialize(SRC_ARRAY := #bufferByteArray,
	                                                     DEST_VARIABLE => #statTimestamps.file,
	                                                     POS := #tempBufferPosition)
	              );


	              // Check if move parameter structure was successful, otherwise set ERROR
	              IF #STATUS_EXECUTION_FINISHED <> #tempResult THEN
	                #statTimestamps.file := #statTimestampDefault;
	                #statInternal.diagnostics.status := #ERR_DESERIALIZE_DATE_TIMESTAMP;
	                #statInternal.diagnostics.subfunctionStatus := #tempResult;
	                EXIT; // Stop execution and leave block
	              END_IF;

	            ELSE
	              #statTimestamps.file := #statTimestampDefault;
	            END_IF;
	          END_REGION

	          // If parameter is byte array - just copy
	          IF #statParameterDataIsByteArray THEN
	            // Copy buffer array into parameter array
	            #tempResult := INT_TO_WORD(
	                                       MOVE_BLK_VARIANT(SRC := #bufferByteArray,
	                                                        COUNT := DINT_TO_UDINT(#statLengthData),
	                                                        SRC_INDEX := #tempBufferPosition,
	                                                        DEST_INDEX := DINT#0,
	                                                        DEST => #data)
	            );

	            // Check if move parameter structure was successful, otherwise set ERROR
	            IF #STATUS_EXECUTION_FINISHED <> #tempResult THEN
	              #statTimestamps.file := #statTimestampDefault;
	              #statInternal.diagnostics.status := #ERR_MOVE_BLK_VARIANT;
	              #statInternal.diagnostics.subfunctionStatus := #tempResult;
	              EXIT; // Stop execution and leave block
	            END_IF;

	            // Next state switched by status
	            #statInternal.diagnostics.status := #STATUS_EXECUTION_FINISHED;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_EXECUTION_FINISHED;

	          ELSE // Parameter is NOT byte array - DESERIALZE
	            // Deserialize data from buffer
	            #tempResult := INT_TO_WORD(
	                                       Deserialize(SRC_ARRAY := #bufferByteArray,
	                                                   DEST_VARIABLE => #data,
	                                                   POS := #tempBufferPosition)
	            );

	            // Check if serialize parameter structure was successful, otherwise set ERROR
	            IF #STATUS_EXECUTION_FINISHED <> #tempResult THEN
	              #statTimestamps.file := #statTimestampDefault;
	              #statInternal.diagnostics.status := #ERR_DESERIALIZE_DATA;
	              #statInternal.diagnostics.subfunctionStatus := #tempResult;
	              EXIT; // Stop execution and leave block
	            END_IF;

	            #statTimestamps.executeRead := #statTimestamps.lastCommand;
	            // Next state switched by status
	            #statInternal.diagnostics.status := #STATUS_EXECUTION_FINISHED;
	            #statInternal.diagnostics.subfunctionStatus := #STATUS_EXECUTION_FINISHED;
	          END_IF;
	        END_REGION

	      #STATE_FILE_WRITE_PARAMETER:
	        REGION File write - Parameter handling
	          // Init buffer size/position pointer
	          #tempBufferPosition := 0;

	          REGION Include time stamp
	            IF #parameter.includeTimestamp THEN
	              // Serialize Time Stamp into buffer
	              // Length of timestamp is 14 byte
	              #statTimestamps.file := #statTimestamps.lastCommand;

	              #tempResult := INT_TO_WORD(
	                                         Serialize(SRC_VARIABLE := #statTimestamps.file,
	                                                   DEST_ARRAY => #bufferByteArray,
	                                                   POS := #tempBufferPosition)
	              );

	              // Check if move parameter structure was successful, otherwise set ERROR
	              IF #STATUS_EXECUTION_FINISHED <> #tempResult THEN
	                #statTimestamps.file := #statTimestampDefault;
	                #statInternal.diagnostics.status := #ERR_SERIALIZE_DATE_TIMESTAMP;
	                #statInternal.diagnostics.subfunctionStatus := #tempResult;
	                EXIT; // Stop execution and leave block
	              END_IF;

	            ELSE
	              #statTimestamps.file := #statTimestampDefault;
	            END_IF;
	          END_REGION

	          IF #statParameterDataIsByteArray THEN // Parameter is byte array - just copy
	            // Copy parameter array into buffer array
	            #tempResult := INT_TO_WORD(
	                                       MOVE_BLK_VARIANT(SRC := #data,
	                                                        COUNT := DINT_TO_UDINT(#statLengthData),
	                                                        SRC_INDEX := DINT#0,
	                                                        DEST_INDEX := #tempBufferPosition,
	                                                        DEST => #bufferByteArray)
	            );
	            // Set data length
	            #statLengthData += #tempBufferPosition;

	            // Check if move parameter structure was successful, otherwise set ERROR
	            IF #STATUS_EXECUTION_FINISHED <> #tempResult THEN
	              #statTimestamps.file := #statTimestampDefault;
	              #statInternal.diagnostics.status := #ERR_MOVE_BLK_VARIANT;
	              #statInternal.diagnostics.subfunctionStatus := #tempResult;
	              EXIT; // Stop execution and leave block
	            END_IF;

	          ELSE // Parameter is NOT byte array - SERIALZE Data
	            // Clear buffer before writing the data into the file
	            // This avoids unintended content inside the binary file
	            IF #parameter.clearBufferBefore THEN
	              FILL_BLK(IN    := #EMPTY_BYTE,
	                       COUNT := DINT_TO_UDINT(#statBufferSize - #tempBufferPosition - 1),
	                       OUT   => #bufferByteArray[#tempBufferPosition]);
	            END_IF;

	            // Serialize data into buffer
	            #tempResult := INT_TO_WORD(
	                                       Serialize(SRC_VARIABLE := #data,
	                                                 DEST_ARRAY => #bufferByteArray,
	                                                 POS := #tempBufferPosition)
	            );

	            // After serialize, set array size pointer to numer of serialized elements
	            #statLengthData := #tempBufferPosition;

	            // Check if serialize parameter structure was successful, otherwise set ERROR
	            IF #STATUS_EXECUTION_FINISHED <> #tempResult THEN
	              #statTimestamps.file := #statTimestampDefault;
	              #statInternal.diagnostics.status := #ERR_SERIALIZE_DATA;
	              #statInternal.diagnostics.subfunctionStatus := #tempResult;
	              EXIT; // Stop execution and leave block
	            END_IF;
	          END_IF;

	          // Next state
	          #statMainState := #STATE_FILE_WRITE;
	          // ASYNCRONUS - set to true:
	          #tempExitStateLoop := #statAsynchronusMode;
	        END_REGION

	      #STATE_FILE_WRITE:
	        REGION File write - File handling
	          // Set file handler request to true
	          #instFileWrite(REQ    := TRUE,
	                         Name   := #fileName,
	                         Offset := UDINT#0,
	                         Length := DINT_TO_UDINT(#statLengthData),
	                         Data   := #bufferByteArray);

	          IF #instFileWrite.Error THEN
	            #statTimestamps.file := #statTimestampDefault;
	            #statTimestamps.executeWrite := #statTimestampDefault;
	            #statLengthFile := UDINT_TO_DINT(#instFileWrite.ResultLength);
	            #statInternal.diagnostics.status := #ERR_FILE_WRITE;
	            #statInternal.diagnostics.subfunctionStatus := #instFileWrite.Status;
	            EXIT; // Stop execution and leave block

	          ELSIF #instFileWrite.Done THEN
	            #statTimestamps.executeWrite := #statTimestamps.lastCommand;

	            #statLengthFile := UDINT_TO_DINT(#instFileWrite.ResultLength);
	            // Next state switched by status
	            #statInternal.diagnostics.status := #STATUS_EXECUTION_FINISHED;
	            #statInternal.diagnostics.subfunctionStatus := 16#0000;
	          END_IF;
	        END_REGION

	      #STATE_FILE_DELETE:
	        REGION File delete
	          // Set file handler request to true
	          #instFileDelete(REQ  := TRUE,
	                          Name := #fileName);

	          IF #instFileDelete.Error THEN
	            #statTimestamps.file := #statTimestampDefault;
	            #statTimestamps.executeDelete := #statTimestampDefault;
	            #statInternal.diagnostics.status := #ERR_FILE_DELETE;
	            #statInternal.diagnostics.subfunctionStatus := #instFileDelete.Status;
	            EXIT; // Stop execution and leave block

	          ELSIF #instFileDelete.Done THEN
	            #statTimestamps.file := #statTimestampDefault;
	            #statTimestamps.executeDelete := #statTimestamps.lastCommand;
	            // Next state switched by status
	            #statInternal.diagnostics.status := #STATUS_EXECUTION_FINISHED;
	            #statInternal.diagnostics.subfunctionStatus := 16#0000;
	          END_IF;
	        END_REGION

	      ELSE // Undefined state in state machine reached
	        #statInternal.diagnostics.status := #ERR_UNDEFINED_STATE;
	    END_CASE;
	    // if one ore more runs in the same cycle are necessary, set #tempExitStateLoop := FALSE;
	    // Check state, if condition = TRUE --> #tempExitStateLoop = TRUE --> LEAVE LOOP
	  UNTIL (TRUE = #tempExitStateLoop)
	  END_REPEAT;
	END_REGION STATE_MACHINE

	REGION OUTPUTS
	  // Write outputs
	  IF (#statInternal.diagnostics.status = #STATUS_EXECUTION_FINISHED) AND (#statInternal.done = FALSE) THEN // Execution finished without errors
	    #statInternal.done := TRUE;
	    #statInternal.busy := FALSE;
	    #statInternal.error := FALSE;
	    #statMainState := #STATE_NO_PROCESSING; // Switch state machine to "no processing"

	  ELSIF (#statInternal.diagnostics.status.%X15 = TRUE) AND (#statInternal.error = FALSE) THEN // Error occurred (#statStatus is 16#8000 to 16#FFFF)
	    #statInternal.done := FALSE;
	    #statInternal.busy := FALSE;
	    #statInternal.error := TRUE;
	    // Write diagnostics
	    #statTimestamps.lastError := #statTimestamps.lastCommand;
	    #statInternal.diagnostics.stateNumber := #statMainState;
	    #diagnostics := #statInternal.diagnostics;
	    #statMainState := #STATE_NO_PROCESSING; // Switch state machine to "no processing"

	  ELSIF (#tempExecute = FALSE) AND ((#statInternal.done = TRUE) OR (#statInternal.error = TRUE)) THEN // Reset outputs
	    #statInternal.done := FALSE;
	    #statInternal.busy := FALSE;
	    #statInternal.error := FALSE;
	    #statInternal.diagnostics.status := #STATUS_NO_CALL;
	    #statLengthData := 0;
	    #statLengthFile := 0;
	  END_IF;

	  // Write static values to outputs
	  #done := #statInternal.done;
	  #busy := #statInternal.busy;
	  #error := #statInternal.error;
	  #status := #statInternal.diagnostics.status;

	  #dataLength := #statLengthData;

	  #fileDoesNotExist := TRUE
	  AND (FALSE
	  OR #statInternal.diagnostics.status = #ERR_FILE_READ
	  OR #statInternal.diagnostics.status = #ERR_FILE_DELETE
	  ) AND (FALSE
	  OR #statInternal.diagnostics.subfunctionStatus = #FILE_DELETE_PATH_NAME_NOT_EXIST
	  OR #statInternal.diagnostics.subfunctionStatus = #FILE_DELETE_FILE_NOT_EXIST
	  );

	  #timestamps := #statTimestamps;

	  ENO := #statInternal.error;
	END_REGION OUTPUTS

END_FUNCTION_BLOCK

