	          // check for min
	          IF #tempActValueReal < #tempMinReal THEN
	            #tempMinReal := #tempActValueReal;
	            #tempArrayIndexMin := #tempCounter;
	            // check for max
	          ELSIF #tempActValueReal > #tempMaxReal THEN
